import React, { useEffect, useState } from 'react';
import { Grid } from '@material-ui/core';
import ToggleOffIcon from '@material-ui/icons/ToggleOff';
import ToggleOnIcon from '@material-ui/icons/ToggleOn';
import VideocamIcon from '@material-ui/icons/Videocam';
import MicIcon from '@material-ui/icons/Mic';

const DisplayPreview = ({ handleSubmit, connecting, setAudio, setVideo, audio, video }) => {
    const dep = 'x';
    const [videoTrack, setVideoTrack] = useState("");
    useEffect(() => {
        const { createLocalVideoTrack } = require('twilio-video');
        const localMediaContainer = document.getElementById('local-media');
        createLocalVideoTrack().then(track => {
            localMediaContainer.appendChild(track.attach());
            setVideoTrack(track);
        });
    }, [dep]);

    const handleClick = () => {
        videoTrack.stop();
    }
    const toggleVideo = () => {
        setVideo(!video);
        if (!video) {
            const { createLocalVideoTrack } = require('twilio-video');
            const localMediaContainer = document.getElementById('local-media');
            localMediaContainer.removeChild(localMediaContainer.childNodes[0]);
            createLocalVideoTrack().then(track => {
                localMediaContainer.appendChild(track.attach());
                setVideoTrack(track);
            });
        } else {
            videoTrack.stop();
        }
    }
    const toggleAudio = () => {
        setAudio(!audio);
    }
    return (
        <Grid container direction='column' justifyContent='center' alignItems='center'>
            <Grid item id='local-media' className='participant'>
            </Grid>
            <Grid item>
                <Grid container direction='row' style = {{marginTop: "10px",  justifyContent : 'space-around'}}>
                    <Grid item>
                        <button onClick={toggleVideo} style = {{display : 'flex', marginRight: "5px"}}>
                            <VideocamIcon style={{ display: 'inline-block' }} />
                            {video ? <ToggleOnIcon style={{ display: 'inline-block' }} /> : <ToggleOffIcon style={{ display: 'inline-block' }} />}
                        </button>
                    </Grid>
                    <Grid item>
                        <form onSubmit={handleSubmit}>
                            <button type="submit" disabled={connecting} onClick={handleClick}  style = {{display : 'flex', marginRight: "5px"}}>
                                {connecting ? <div><i className="fa fa-spinner fa-spin"></i> <span>Connecting</span></div> : "Connect"}
                            </button>
                        </form>
                    </Grid>
                    <Grid item>
                        <button onClick={toggleAudio} style = {{display: "flex"}}>
                            <MicIcon style={{ display: 'inline-block' }} />
                            {audio ? <ToggleOnIcon style={{ display: 'inline-block' }} /> : <ToggleOffIcon style={{ display: 'inline-block' }} />}
                        </button>
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
            );
}
            export default DisplayPreview;
