import React, { useEffect } from 'react';
import { Grid } from '@material-ui/core';

const DisplayPreview = ({ handleSubmit, connecting, setConnecting }) => {
    const dep = 'x';
    var videoTrack = "";
    useEffect(() => {
        const { createLocalVideoTrack } = require('twilio-video');
        const localMediaContainer = document.getElementById('local-media');
        createLocalVideoTrack().then(track => {
            localMediaContainer.appendChild(track.attach());
            videoTrack = track;
        });
    }, [dep, videoTrack]);

    const handleClick = () => {
        videoTrack.stop();
    }

    return (
        <Grid container direction='row' justifyContent='center' alignItems='center'>
            <Grid item id = 'local-media' className = 'participant'>
                {/* <div id='local-media' className='participant'></div> */}
            </Grid>
            <Grid item>
                <form onSubmit={handleSubmit}>
                    <button type="submit" disabled={connecting} onClick={handleClick}>
                        {connecting ? <div><i className="fa fa-spinner fa-spin"></i> <span>Connecting</span></div> : "Connect"}
                    </button>
                </form>
            </Grid>
        </Grid>
    );
}
export default DisplayPreview;
