import React, { useState, useCallback, useEffect } from "react";
import Video from "twilio-video";
import Lobby from "./Lobby";
import Room from "./Room";
import RoomHeader from "./toolbar/RoomHeader";
import ChatScreen from "./ChatUtils";
import DisplayPreview from "./DisplayPreview";
// child to App.js
// VideoChat.js handles data about the chat

const VideoChat = (props) => {

  // state variables
  const [username, setUsername] = useState(props.username); //username
  const [roomName, setRoomName] = useState(props.roomName); //roomname
  const [room, setRoom] = useState(null); //room details
  const [connecting, setConnecting] = useState(false); //current status
  const [disconnect, setDisconnect] = useState(false);

  //update username on change
  const handleUsernameChange = useCallback((event) => {
    setUsername(event.target.value);
  }, []);

  // update room name on change
  const handleRoomNameChange = useCallback((event) => {
    setRoomName(event.target.value);
  }, []);

  // Method to send Username & Roomname to the server
  // And recieve an access token
  const handleSubmit = useCallback(
    async (event) => {

      event.preventDefault(); 
      setConnecting(true); // connecting to Twilio API

      const data = await fetch("/video/token", {
        method: "POST",
        body: JSON.stringify({
          identity: username,
          room: roomName,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      }).then((res) => res.json());

      // connect to the room using twilio API
      Video.connect(data.token, {
        name: roomName,
        dominantSpeaker : true //enable dominant speaker feature
      }).then((room) => {
          setConnecting(false);
          setRoom(room);
        }).catch((err) => {
          console.error(err);
          setConnecting(false);
        });
    },
    [roomName, username]
  );

  // Ejects the user from the room and puts in the Lobby
  const handleLogout = useCallback((event) => {
    event.preventDefault();
    document.title = "Microsoft Engage 2021"
    setRoom((prevRoom) => {
      if (prevRoom) {

        // stop media tracks before disconnecting from room
        prevRoom.localParticipant.tracks.forEach((trackPub) => {
          if(trackPub.track.kind !== 'data'){
            trackPub.track.stop();
            if(trackPub.track.kind === 'video'){
                console.log("CALLED");
                trackPub.unpublish();
                console.log(trackPub);
            }
          }
        });

        // disconnect from room
        prevRoom.disconnect();
      }
      return null;
    });
    setDisconnect(true);
  }, []);

  useEffect(() => {
    if (room) {
      const tidyUp = (event) => {
        if (event.persisted) {
          return;
        }
        if (room) {
          handleLogout();
        }
      };
      window.addEventListener("pagehide", tidyUp);
      window.addEventListener("beforeunload", tidyUp);
      return () => {
        window.removeEventListener("pagehide", tidyUp);
        window.removeEventListener("beforeunload", tidyUp);
      };
    }
  }, [room, handleLogout]);

  let render;

  // if room already exists then render Room.js
  if (room) {
    render = (
      <>
      <Room roomName={roomName} room={room} handleLogout={handleLogout} />
      <footer>
        <RoomHeader handleLogout = {handleLogout} room = {room} roomName = {roomName}/>
      </footer>
      </>
    );
  } else if(disconnect){ 
      props.setIsVideo(false);
      console.log(document.getElementById('local-media'));
      render = <div></div>
  }else{
      // If room doesnt exists then render Lobby.js
    render = (
        // <Lobby
        //   username={username}
        //   roomName={roomName}
        //   handleUsernameChange={handleUsernameChange}
        //   handleRoomNameChange={handleRoomNameChange}
        //   handleSubmit={handleSubmit}
        //   connecting={connecting}
        // />
        <DisplayPreview handleSubmit = {handleSubmit} connecting = {connecting} setConnecting = {setConnecting}/>
        // <div></div>
      );
  }
  return render;
};

export default VideoChat;
