import React, { useState, useEffect, useRef } from "react";


const Participant = ({ participant, local }) => {
  const [videoTracks, setVideoTracks] = useState([]);
  const [audioTracks, setAudioTracks] = useState([]);

  const videoRef = useRef();
  const audioRef = useRef();

  // Area:
function Area(Increment, Count, Width, Height, Margin = 10) {
  let i=0, w = 0;
  let h = Increment * 0.75 + (Margin * 2);
  while (i < (Count)) {
      if ((w + Increment) > Width) {
          w = 0;
          h = h + (Increment * 0.75) + (Margin * 2);
      }
      w = w + Increment + (Margin * 2);
      i++;
  }
  if (h > Height) return false;
  else return Increment;
}
// Dish:
function Dish() {

  // variables:
      let Margin = 2;
      let Scenary = document.getElementById('root');
      console.log(Scenary);
      let Width = Scenary.offsetWidth - (Margin * 2);
      let Height = Scenary.offsetHeight - (Margin * 2);
      let Cameras = document.getElementsByTagName('video');
      let max = 0;

  // loop (i recommend you optimize this)
      let i = 1;
      while (i < 5000) {
          let w = Area(i, Cameras.length, Width, Height, Margin);
          if (w === false) {
              max =  i - 1;
              break;
          }
          i++;
      }

  // set styles
      max = max - (Margin * 2);
      setWidth(max, Margin);
      console.log(max, Cameras);
}

// Set Width and Margin 
function setWidth(width, margin) {
  let Cameras = document.getElementsByTagName('video');
  for (var s = 0; s < Cameras.length; s++) {
      Cameras[s].style.width = width + "px";
      Cameras[s].style.margin = margin + "px";
      Cameras[s].style.height = (width * 0.75) + "px";
  }
}

  useEffect(()=>{
    // const {largestRect} = require('rect-scaler');
    // const container = document.getElementById('participants');
    // const containerWidth = container.getBoundingClientRect().width;
    // const containerHeight = container.getBoundingClientRect().height;
    // const vids = document.getElementsByTagName('video');
    // const numRects = document.getElementsByTagName('video').length;
    // const rectWidth = document.getElementsByTagName('video')[0].getBoundingClientRect().width;
    // const rectHeight = document.getElementsByTagName('video')[0].getBoundingClientRect().height;
    // const result = largestRect(containerWidth, containerHeight, numRects, rectWidth, rectHeight);
    // for(let i = 0; i < numRects; i++){
    //   vids[i].style.width = result.width + "px";
    //   vids[i].style.height = result.height+"px";
    // }
    // console.log(result);
    // console.log(result);
    Dish();
  });

  const trackpubsToTracks = (trackMap) =>
    Array.from(trackMap.values())
      .map((publication) => publication.track)
      .filter((track) => track !== null);

  useEffect(() => {
    setVideoTracks(trackpubsToTracks(participant.videoTracks));
    setAudioTracks(trackpubsToTracks(participant.audioTracks));

    const trackSubscribed = (track) => {
      if (track.kind === "video") {
        setVideoTracks((videoTracks) => [...videoTracks, track]);
      } else if (track.kind === "audio") {
        setAudioTracks((audioTracks) => [...audioTracks, track]);
      } else if (track.kind === 'data'){
          track.on('message', data => {
            const node = document.createElement('div');
            let author = JSON.parse(data)['author'];
            let message = JSON.parse(data)['data'];
            const authorspan = document.createElement('span');
            authorspan.style.fontWeight = 'bold';
            const authornode = document.createTextNode(author + ": ");
            authorspan.appendChild(authornode);
            const textnode = document.createTextNode(message);
            const textspan = document.createElement('span');
            textspan.appendChild(textnode);
            const wrapper = document.createElement('span');
            wrapper.appendChild(authorspan);
            wrapper.appendChild(textspan);
            node.appendChild(wrapper);
            document.getElementById('text-area').appendChild(node);
        });
      }
    };

    const trackUnsubscribed = (track) => {
      if (track.kind === "video") {
        setVideoTracks((videoTracks) => videoTracks.filter((v) => v !== track));
      } else if (track.kind === "audio") {
        setAudioTracks((audioTracks) => audioTracks.filter((a) => a !== track));
      } 
    };

    participant.on("trackSubscribed", trackSubscribed);
    participant.on("trackUnsubscribed", trackUnsubscribed);

    return () => {
      setVideoTracks([]);
      setAudioTracks([]);
      participant.removeAllListeners();
    };
  }, [participant]);

  useEffect(() => {
    const videoTrack = videoTracks[0];
    if (videoTrack) {
      videoTrack.attach(videoRef.current);
      // const blurBackground = new GaussianBlurBackgroundProcessor({
      //   assetsPath: 'assets'
      // });
      // blurBackground.loadModel().then(() => {
      //     videoTrack.addProcessor(blurBackground);
      // });
      return () => {
        videoTrack.detach();
      };
    }
  }, [videoTracks]);

  useEffect(() => {
    const audioTrack = audioTracks[0];
    if (audioTrack) {
      audioTrack.attach(audioRef.current);
      return () => {
        audioTrack.detach();
      };
    }
  }, [audioTracks]);
  return (
    <div className="participant" id ={participant.sid}>
      <div className = 'video-container'>
        <video ref={videoRef} autoPlay={true}/>
      </div>
      <audio ref={audioRef} autoPlay={true} muted={false} />
      <p className = 'overlayName'>{local?"You":participant.identity}</p>
    </div>
  );
};

export default Participant;
