    return this.state.body;
  }

  public get dateUpdated(): Date { return this.state.dateUpdated; }

  public get index(): number { return this.state.index; }

  public get lastUpdatedBy(): string { return this.state.lastUpdatedBy; }

  public get dateCreated(): Date { return this.state.timestamp; }

  public get attributes(): Object { return this.state.attributes; }

  public get type(): Message.Type { return this.state.type; }

  public get media(): Media { return this.state.media; }

  public get participantSid(): string { return this.state.participantSid; }

  public get aggregatedDeliveryReceipt(): AggregatedDeliveryReceipt | null { return this.state.aggregatedDeliveryReceipt; }

  _update(data) {
    let updateReasons: Message.UpdateReason[] = [];

    if ((data.text || ((typeof data.text) === 'string')) && data.text !== this.state.body) {
      this.state.body = data.text;
      updateReasons.push('body');
    }

    if (data.subject && data.subject !== this.state.subject) {
      this.state.subject = data.subject;
      updateReasons.push('subject');
    }

    if (data.lastUpdatedBy && data.lastUpdatedBy !== this.state.lastUpdatedBy) {
      this.state.lastUpdatedBy = data.lastUpdatedBy;
      updateReasons.push('lastUpdatedBy');
    }

    if (data.author && data.author !== this.state.author) {
      this.state.author = data.author;
      updateReasons.push('author');
    }

    if (data.dateUpdated &&
      new Date(data.dateUpdated).getTime() !== (this.state.dateUpdated && this.state.dateUpdated.getTime())) {
      this.state.dateUpdated = new Date(data.dateUpdated);
      updateReasons.push('dateUpdated');
    }

    if (data.timestamp &&
      new Date(data.timestamp).getTime() !== (this.state.timestamp && this.state.timestamp.getTime())) {
      this.state.timestamp = new Date(data.timestamp);
      updateReasons.push('dateCreated');
    }

    let updatedAttributes = parseAttributes(data.attributes, `Got malformed attributes for the message ${this.sid}`, log);
    if (!isDeepEqual(this.state.attributes, updatedAttributes)) {
      this.state.attributes = updatedAttributes;
      updateReasons.push('attributes');
    }

    let updatedAggregatedDelivery = data.delivery;
    let currentAggregatedDelivery = this.state.aggregatedDeliveryReceipt;
    let isUpdatedAggregateDeliveryValid = !!updatedAggregatedDelivery && !!updatedAggregatedDelivery.total &&
      !!updatedAggregatedDelivery.delivered && !!updatedAggregatedDelivery.failed && !!updatedAggregatedDelivery.read &&
      !!updatedAggregatedDelivery.sent && !!updatedAggregatedDelivery.undelivered;
    if (isUpdatedAggregateDeliveryValid) {
      if (!currentAggregatedDelivery) {
        this.state.aggregatedDeliveryReceipt = new AggregatedDeliveryReceipt(updatedAggregatedDelivery);
        updateReasons.push('deliveryReceipt');
      } else if (!currentAggregatedDelivery._isEquals(updatedAggregatedDelivery)) {
        currentAggregatedDelivery._update(updatedAggregatedDelivery);
        updateReasons.push('deliveryReceipt');
      }
    }

    if (updateReasons.length > 0) {
      this.emit('updated', { message: this, updateReasons: updateReasons });
    }
  }

  /**
   * Get Participant who is author of the Message
   * @returns {Promise<Participant>}
   */
  async getParticipant(): Promise<Participant> {
    let participant: Participant = null;
    if (this.state.participantSid) {
      participant = await this.conversation.getParticipantBySid(this.participantSid)
        .catch(() => {
          log.debug('Participant with sid "' + this.participantSid + '" not found for message ' + this.sid);
          return null;
        });
    }
    if (!participant && this.state.author) {
      participant = await this.conversation.getParticipantByIdentity(this.state.author)
        .catch(() => {
          log.debug('Participant with identity "' + this.author + '" not found for message ' + this.sid);
          return null;
        });
    }
    if (participant) {
      return participant;
    }
    let errorMesage = 'Participant with ';
    if (this.state.participantSid) {
      errorMesage += 'SID \'' + this.state.participantSid + '\' ';
    }
    if (this.state.author) {
      if (this.state.participantSid) {
        errorMesage += 'or ';
      }
      errorMesage += 'identity \'' + this.state.author + '\' ';
    }
    if (errorMesage === 'Participant with ') {
      errorMesage = 'Participant ';
    }
    errorMesage += 'was not found';
    throw new Error(errorMesage);
  }

  /**
   * Get delivery receipts of the message
   * @returns {Promise<DetailedDeliveryReceipt[]>}
   */
  async getDetailedDeliveryReceipts(): Promise<DetailedDeliveryReceipt[]> {
    let paginator: Paginator<DetailedDeliveryReceipt> = await this._getDetailedDeliveryReceiptsPaginator();
    let detailedDeliveryReceipts: DetailedDeliveryReceipt[] = [];

    while (true) {
      detailedDeliveryReceipts = [...detailedDeliveryReceipts, ...paginator.items];

      if (!paginator.hasNextPage) {
        break;
      }

      paginator = await paginator.nextPage();
    }

    return detailedDeliveryReceipts;
  }

  /**
   * Remove the Message.
   * @returns {Promise<Message>}
   */
  async remove(): Promise<Message> {
    await this.services.session.addCommand('deleteMessage', {
      channelSid: this.conversation.sid,
      messageIdx: this.index.toString()
    });
    return this;
  }

  /**
   * Edit message body.
   * @param {String} body - new body of Message.
   * @returns {Promise<Message>}
   */
  @validateTypesAsync('string')
  async updateBody(body: string): Promise<Message> {
    await this.services.session.addCommand('editMessage', {
      channelSid: this.conversation.sid,
      messageIdx: this.index.toString(),
      text: body
    });

    return this;
  }

  /**
   * Edit message attributes.
   * @param {any} attributes new attributes for Message.
   * @returns {Promise<Message>}
   */
  @validateTypesAsync(['string', 'number', 'boolean', 'object', literal(null)])
  async updateAttributes(attributes: any): Promise<Message> {
    await this.services.session.addCommand('editMessageAttributes', {
      channelSid: this.conversation.sid,
      messageIdx: this.index,
      attributes: JSON.stringify(attributes)
    });

    return this;
  }

  private async _getDetailedDeliveryReceiptsPaginator(options?: {
    pageToken?: string,
    pageSize?: number
  }): Promise<Paginator<DetailedDeliveryReceipt>> {
    let links = await this.services.session.getSessionLinks();
    let messagesReceiptsUrl = links.messagesReceiptsUrl.replace('%s', this.conversation.sid).replace('%s', this.sid);
    let url = new UriBuilder(messagesReceiptsUrl).arg('PageToken', options?.pageToken).arg('PageSize', options?.pageSize).build();
    let response = await this.services.network.get(url);

    return new RestPaginator<DetailedDeliveryReceipt>(response.body.delivery_receipts.map(x => new DetailedDeliveryReceipt(x))
      , (pageToken, pageSize) => this._getDetailedDeliveryReceiptsPaginator({ pageToken, pageSize })
      , response.body.meta.previous_token
      , response.body.meta.next_token);
  }
}

export { Message };

/**
 * Fired when the Message's properties or body has been updated.
 * @event Message#updated
 * @type {Object}
 * @property {Message} message - Updated Message
 * @property {Message#UpdateReason[]} updateReasons - Array of Message's updated event reasons
 */
import { EventEmitter } from 'events';
import { Logger } from '../logger';

import { Message } from '../message';
import { Conversation } from '../conversation';

import { SyncList, SyncClient } from 'twilio-sync';
import { SyncPaginator } from '../syncpaginator';

import { Session } from '../session';
import { McsClient, McsMedia } from 'twilio-mcs-client';
import { Network } from '../services/network';

const log = Logger.scope('Messages');

export interface MessagesServices {
  session: Session;
  mcsClient: McsClient;
  network: Network;
  syncClient: SyncClient;
}

/**
 * Represents the collection of messages in a conversation
 */
class Messages extends EventEmitter {
  private readonly services: MessagesServices;
  private readonly messagesByIndex: Map<number, Message>;
  private messagesListPromise: Promise<SyncList>;

  public readonly conversation: Conversation;

  constructor(conversation: Conversation, services: MessagesServices) {
    super();

    this.conversation = conversation;
    this.services = services;

    this.messagesByIndex = new Map();
    this.messagesListPromise = null;
  }

  /**
   * Subscribe to the Messages Event Stream
   * @param {String} name - The name of Sync object for the Messages resource.
   * @returns {Promise}
   */
  subscribe(name: string) {
    return this.messagesListPromise =
      this.messagesListPromise ||
      this.services.syncClient.list({ id: name, mode: 'open_existing' })
          .then(list => {

            list.on('itemAdded', args => {
              log.debug(this.conversation.sid + ' itemAdded: ' + args.item.index);
              let message = new Message(this.conversation, this.services, args.item.index, args.item.data);
              if (this.messagesByIndex.has(message.index)) {
                log.debug('Message arrived, but already known and ignored', this.conversation.sid, message.index);
                return;
              }

              this.messagesByIndex.set(message.index, message);
              message.on('updated',
                (args: Message.UpdatedEventArgs) => this.emit('messageUpdated', args));
              this.emit('messageAdded', message);
            });

            list.on('itemRemoved', args => {
              log.debug(this.conversation.sid + ' itemRemoved: ' + args.index);
              let index = args.index;
              if (this.messagesByIndex.has(index)) {
                let message = this.messagesByIndex.get(index);
                this.messagesByIndex.delete(message.index);
                message.removeAllListeners('updated');
                this.emit('messageRemoved', message);
              }
            });

            list.on('itemUpdated', args => {
              log.debug(this.conversation.sid + ' itemUpdated: ' + args.item.index);
              let message = this.messagesByIndex.get(args.item.index);
              if (message) {
                message._update(args.item.data);
              }
            });

            return list;
          })
          .catch(err => {
            this.messagesListPromise = null;
            if (this.services.syncClient.connectionState != 'disconnected') {
              log.error('Failed to get messages object for conversation', this.conversation.sid, err);
            }
            log.debug('ERROR: Failed to get messages object for conversation', this.conversation.sid, err);
            throw err;
          });
  }

  async unsubscribe() {
    if (this.messagesListPromise) {
      let entity = await this.messagesListPromise;
      entity.close();
      this.messagesListPromise = null;
    }
  }

  /**
   * Send Message to the conversation
   * @param {String} message - Message to post
   * @param {any} attributes Message attributes
   * @param {Conversation.SendEmailOptions} emailOptions Options that modify E-mail integration behaviors.
   * @returns Returns promise which can fail
   */
  async send(message: string, attributes: any = {}, emailOptions?: Conversation.SendEmailOptions) {
    log.debug('Sending text message', message, attributes, emailOptions);

    return this.services.session.addCommand('sendMessage', {
      channelSid: this.conversation.sid,
      text: message,
      attributes: JSON.stringify(attributes),
      subject: emailOptions?.subject,
    });
  }

  /**
   * Send Media Message to the conversation
   * @param {FormData | Conversation#SendMediaOptions} mediaContent - Media content to post
   * @param {any} attributes Message attributes
   * @returns Returns promise which can fail
   */
  async sendMedia(mediaContent: FormData | Conversation.SendMediaOptions, attributes: any = {}, emailOptions?: Conversation.SendEmailOptions) {
    log.debug('Sending media message', mediaContent, attributes, emailOptions);

    let media: McsMedia;
    if (typeof FormData !== 'undefined'  && (mediaContent instanceof FormData)) {
      log.debug('Sending media message as FormData', mediaContent, attributes);
      media = await this.services.mcsClient.postFormData(mediaContent);
    } else {
      log.debug('Sending media message as SendMediaOptions', mediaContent, attributes);
      let mediaOptions = mediaContent as Conversation.SendMediaOptions;
      if (!mediaOptions.contentType || !mediaOptions.media) {
        throw new Error('Media content <Conversation#SendMediaOptions> must contain non-empty contentType and media');
      }
      media = await this.services.mcsClient.post(mediaOptions.contentType, mediaOptions.media);
    }
    // emailOptions are currently ignored for media messages.
    return this.services.session.addCommand('sendMediaMessage', {
      channelSid: this.conversation.sid,
      mediaSid: media.sid,
      attributes: JSON.stringify(attributes)
    });
  }

  /**
   * Returns messages from conversation using paginator interface
   * @param {Number} [pageSize] Number of messages to return in single chunk. By default it's 30.
   * @param {String} [anchor] Most early message id which is already known, or 'end' by default
   * @param {String} [direction] Pagination order 'backwards' or 'forward', or 'forward' by default
   * @returns {Promise<Paginator<Message>>} last page of messages by default
   */
  getMessages(pageSize, anchor, direction) {
    anchor = (typeof anchor !== 'undefined') ? anchor : 'end';
    direction = direction || 'backwards';
    return this._getMessages(pageSize, anchor, direction);
  }

  private wrapPaginator(order, page, op) {
    // We should swap next and prev page here, because of misfit of Sync and Chat paging conceptions
    let shouldReverse = order === 'desc';

    let np = () => page.nextPage().then(x => this.wrapPaginator(order, x, op));
    let pp = () => page.prevPage().then(x => this.wrapPaginator(order, x, op));

    return op(page.items).then(items => ({
      items: items.sort((x, y) => { return x.index - y.index; }),
      hasPrevPage: shouldReverse ? page.hasNextPage : page.hasPrevPage,
      hasNextPage: shouldReverse ? page.hasPrevPage : page.hasNextPage,
      prevPage: shouldReverse ? np : pp,
      nextPage: shouldReverse ? pp : np
    }));
  }

  private _upsertMessage(index: number, value: string) {
    let cachedMessage = this.messagesByIndex.get(index);
    if (cachedMessage) {
      return cachedMessage;
    }

    let message = new Message(this.conversation, this.services, index, value);
    this.messagesByIndex.set(message.index, message);
    message.on('updated',
      (args: Message.UpdatedEventArgs) => this.emit('messageUpdated', args));
    return message;
  }

  /**
   * Returns last messages from conversation
   * @param {Number} [pageSize] Number of messages to return in single chunk. By default it's 30.
   * @param {String} [anchor] Most early message id which is already known, or 'end' by default
   * @param {String} [direction] Pagination order 'backwards' or 'forward', or 'forward' by default
   * @returns {Promise<SyncPaginator<Message>>} last page of messages by default
   * @private
   */
  private _getMessages(pageSize, anchor, direction): Promise<SyncPaginator<Message>> {
    anchor = (typeof anchor !== 'undefined') ? anchor : 'end';
    pageSize = pageSize || 30;
    let order = direction === 'backwards' ? 'desc' : 'asc';

    return this.messagesListPromise
               .then(messagesList => messagesList.getItems({
                 from: anchor !== 'end' ? anchor : void (0),
                 pageSize,
                 order
               }))
               .then(page => this.wrapPaginator(order, page
                 , items => Promise.all(items.map(item => this._upsertMessage(item.index, item.data))))
               );
  }
}

export { Messages };
import { EventEmitter } from 'events';
import { Logger } from './logger';

import { Participants } from './data/participants';
import { Participant } from './participant';
import { Messages } from './data/messages';
import { Message } from './message';

import { UriBuilder, isDeepEqual, parseToNumber } from './util';
import { Users } from './data/users';
import { Paginator } from './interfaces/paginator';
import { Conversations } from './data/conversations';
import { McsClient } from 'twilio-mcs-client';

import { SyncClient } from 'twilio-sync';
import { Session } from './session';
import { ReadHorizon } from './services/readhorizon';
import { TypingIndicator } from './services/typingindicator';
import { Network } from './services/network';
import { validateTypesAsync, custom, literal, nonEmptyString, nonNegativeInteger, objectSchema } from 'twilio-sdk-type-validator';

const log = Logger.scope('Conversation');

const fieldMappings = {
  lastMessage: 'lastMessage',
  attributes: 'attributes',
  createdBy: 'createdBy',
  dateCreated: 'dateCreated',
  dateUpdated: 'dateUpdated',
  friendlyName: 'friendlyName',
  lastConsumedMessageIndex: 'lastConsumedMessageIndex',
  notificationLevel: 'notificationLevel',
  sid: 'sid',
  status: 'status',
  uniqueName: 'uniqueName',
  state: 'state'
};

function parseTime(timeString) {
  try {
    return new Date(timeString);
  } catch (e) {
    return null;
  }
}

export interface ConversationServices {
  session: Session;
  users: Users;
  typingIndicator: TypingIndicator;
  readHorizon: ReadHorizon;
  network: Network;
  mcsClient: McsClient;
  syncClient: SyncClient;
}

interface ConversationState {
  uniqueName: string;
  status: Conversation.Status;
  attributes: any;
  createdBy?: string;
  dateCreated: Date;
  dateUpdated: Date;
  friendlyName?: string;
  lastReadMessageIndex: number | null;
  lastMessage?: Conversation.LastMessage;
  notificationLevel?: Conversation.NotificationLevel;
  state?: Conversation.State;
}

interface ConversationDescriptor {
  channel: string;
  entityName: string;
  uniqueName: string;
  attributes: any;
  createdBy?: string;
  friendlyName?: string;
  lastConsumedMessageIndex: number;
  dateCreated: any;
  dateUpdated: any;
  notificationLevel?: Conversation.NotificationLevel;
}

namespace Conversation {
  export type UpdateReason = 'attributes' | 'createdBy' | 'dateCreated' | 'dateUpdated' |
    'friendlyName' | 'lastReadMessageIndex' | 'state' | 'status' | 'uniqueName' | 'lastMessage' | 'notificationLevel';

  export type Status = 'notParticipating' | 'joined';

  export type NotificationLevel = 'default' | 'muted';

  export type State = {
    current: 'active' | 'inactive' | 'closed',
    dateUpdated: Date
  } | undefined;

  export interface UpdatedEventArgs {
    conversation: Conversation;
    updateReasons: Conversation.UpdateReason[];
  }

  export interface SendMediaOptions {
    contentType: string;
    media: string | Buffer;
  }

  export interface SendEmailOptions {
    subject?: string;
  }

  export interface LastMessage {
    index?: number;
    dateCreated?: Date;
  }
}

/**
 * @classdesc A Conversation represents communication between multiple Conversations Clients
 * @property {any} attributes - The Conversation's custom attributes
 * @property {String} createdBy - The identity of the User that created this Conversation
 * @property {Date} dateCreated - The Date this Conversation was created
 * @property {Date} dateUpdated - The Date this Conversation was last updated
 * @property {String} [friendlyName] - The Conversation's name
 * @property {Number|null} lastReadMessageIndex - Index of the last Message the User has read in this Conversation
 * @property {Conversation#LastMessage} lastMessage - Last Message sent to this Conversation
 * @property {Conversation#NotificationLevel} notificationLevel - User Notification level for this Conversation
 * @property {String} sid - The Conversation's unique system identifier
 * @property {Conversation#Status} status - The Conversation's status
 * @property {Conversation#State} state - The Conversation's state
 * @property {String} uniqueName - The Conversation's unique name
 * @fires Conversation#participantJoined
 * @fires Conversation#participantLeft
 * @fires Conversation#participantUpdated
 * @fires Conversation#messageAdded
 * @fires Conversation#messageRemoved
 * @fires Conversation#messageUpdated
 * @fires Conversation#typingEnded
 * @fires Conversation#typingStarted
 * @fires Conversation#updated
 * @fires Conversation#removed
 */
class Conversation extends EventEmitter {
  public readonly sid: string;

  private services: ConversationServices;
  private channelState: ConversationState;
  private statusSource: Conversations.DataSource;

  private entityPromise: Promise<any>;
  private entityName: string;
  private entity: any;
  private messagesEntity: any;
  private participantsEntity: Participants;
  private participants: any;

  /**
   * These options can be passed to {@link Conversation#sendMessage}.
   * @typedef {Object} Conversation#SendMediaOptions
   * @property {String} contentType - content type of media
   * @property {String | Buffer} media - content to post
   */

  /**
   * These options can be passed to {@link Conversation#sendMessage}.
   * @typedef {Object} Conversation#SendEmailOptions
   * @property {String} subject - subject for the message. Ignored for media messages.
   */

  /**
   * The update reason for <code>updated</code> event emitted on Conversation
   * @typedef {('attributes' | 'createdBy' | 'dateCreated' | 'dateUpdated' |
    'friendlyName' | 'lastReadMessageIndex' | 'state' | 'status' | 'uniqueName' | 'lastMessage' |
    'notificationLevel' )} Conversation#UpdateReason
   */

  /**
   * The status of the Conversation, relative to the Client: whether the Conversation has been <code>joined</code> or the Client is
   * <code>notParticipating</code> in the Conversation.
   * @typedef {('notParticipating' | 'joined')} Conversation#Status
   */

  /**
   * The User's Notification level for Conversation, determines whether the currently logged-in User will receive
   * pushes for events in this Conversation. Can be either <code>muted</code> or <code>default</code>,
   * where <code>default</code> defers to global Service push configuration.
   * @typedef {('default' | 'muted')} Conversation#NotificationLevel
   */

  /**
   * The Conversation's state.
   * @typedef {Object} Conversation#State
   * @property {('active' | 'inactive' | 'closed')} current - the current state
   * @property {Date} dateUpdated - date at which the latest conversation state update happened
   */

  constructor(services: ConversationServices, descriptor: ConversationDescriptor, sid: string) {
    super();

    let attributes = descriptor.attributes || {};
    let createdBy = descriptor.createdBy;
    let dateCreated = parseTime(descriptor.dateCreated);
    let dateUpdated = parseTime(descriptor.dateUpdated);
    let friendlyName = descriptor.friendlyName || null;
    let lastReadMessageIndex =
      Number.isInteger(descriptor.lastConsumedMessageIndex) ? descriptor.lastConsumedMessageIndex : null;
    let uniqueName = descriptor.uniqueName || null;

    try {
      JSON.stringify(attributes);
    } catch (e) {
      throw new Error('Attributes must be a valid JSON object.');
    }

    this.services = services;

    this.sid = sid;
    this.entityName = descriptor.channel;
    this.channelState = {
      uniqueName,
      status: 'notParticipating',
      attributes,
      createdBy,
      dateCreated,
      dateUpdated,
      friendlyName,
      lastReadMessageIndex: lastReadMessageIndex
    };

    if (descriptor.notificationLevel) {
      this.channelState.notificationLevel = descriptor.notificationLevel;
    }

    this.participants = new Map();
    this.participantsEntity = new Participants(this, this.services, this.participants);
    this.participantsEntity.on('participantJoined', this.emit.bind(this, 'participantJoined'));
    this.participantsEntity.on('participantLeft', this.emit.bind(this, 'participantLeft'));
    this.participantsEntity.on('participantUpdated',
      (args: Participant.UpdatedEventArgs) => this.emit('participantUpdated', args));

    this.messagesEntity = new Messages(this, services);
    this.messagesEntity.on('messageAdded', message => this._onMessageAdded(message));
    this.messagesEntity.on('messageUpdated',
      (args: Message.UpdatedEventArgs) => this.emit('messageUpdated', args));
    this.messagesEntity.on('messageRemoved', this.emit.bind(this, 'messageRemoved'));
  }

  public get uniqueName(): string { return this.channelState.uniqueName; }

  public get status(): Conversation.Status { return this.channelState.status; }

  public get friendlyName(): string { return this.channelState.friendlyName; }

  public get dateUpdated(): any { return this.channelState.dateUpdated; }

  public get dateCreated(): any { return this.channelState.dateCreated; }

  public get createdBy(): string { return this.channelState.createdBy; }

  public get attributes(): Object { return this.channelState.attributes; }

  public get lastReadMessageIndex(): number | null { return this.channelState.lastReadMessageIndex; }

  public get lastMessage(): Conversation.LastMessage { return this.channelState.lastMessage; }

  public get notificationLevel(): Conversation.NotificationLevel { return this.channelState.notificationLevel; }

  public get state(): Conversation.State { return this.channelState.state; }

  /**
   * The Conversation's last message's information.
   * @typedef {Object} Conversation#LastMessage
   * @property {Number} index - Message's index
   * @property {Date} dateCreated - Message's creation date
   */

  /**
   * Load and Subscribe to this Conversation and do not subscribe to its Participants and Messages.
   * This or _subscribeStreams will need to be called before any events on Conversation will fire.
   * @returns {Promise}
   * @private
   */
  _subscribe() {
    if (this.entityPromise) { return this.entityPromise; }

    return this.entityPromise = this.entityPromise ||
      this.services.syncClient.document({ id: this.entityName, mode: 'open_existing' })
        .then(entity => {
          this.entity = entity;
          this.entity.on('updated', args => { this._update(args.data); });
          this.entity.on('removed', () => this.emit('removed', this));
          this._update(this.entity.data);
          return entity;
        })
        .catch(err => {
          this.entity = null;
          this.entityPromise = null;
          if (this.services.syncClient.connectionState != 'disconnected') {
            log.error('Failed to get conversation object', err);
          }
          log.debug('ERROR: Failed to get conversation object', err);
          throw err;
        });
  }

  /**
   * Load the attributes of this Conversation and instantiate its Participants and Messages.
   * This or _subscribe will need to be called before any events on Conversation will fire.
   * This will need to be called before any events on Participants or Messages will fire
   * @returns {Promise}
   * @private
   */
  async _subscribeStreams() {
    try {
      await this._subscribe();
      log.trace('_subscribeStreams, this.entity.data=', this.entity.data);
      const messagesObjectName = this.entity.data.messages;
      const rosterObjectName = this.entity.data.roster;
      await Promise.all([
        this.messagesEntity.subscribe(messagesObjectName),
        this.participantsEntity.subscribe(rosterObjectName)
      ]);
    } catch (err) {
      if (this.services.syncClient.connectionState !== 'disconnected') {
        log.error('Failed to subscribe on conversation objects', this.sid, err);
      }
      log.debug('ERROR: Failed to subscribe on conversation objects', this.sid, err);
      throw err;
    }
  }

  /**
   * Stop listening for and firing events on this Conversation.
   * @returns {Promise}
   * @private
   */
  async _unsubscribe() {
    if (this.entity) {
      await this.entity.close();
      this.entity = null;
      this.entityPromise = null;
    }

    return Promise.all([
      this.participantsEntity.unsubscribe(),
      this.messagesEntity.unsubscribe()
    ]);
  }

  /**
   * Set conversation status
   * @private
   */
  _setStatus(status: Conversation.Status, source: Conversations.DataSource) {
    this.statusSource = source;

    if (this.channelState.status === status) { return; }

    this.channelState.status = status;

    if (status === 'joined') {
      this._subscribeStreams()
        .catch(err => {
          log.debug('ERROR while setting conversation status ' + status, err);
          if (this.services.syncClient.connectionState !== 'disconnected') {
            throw err;
          }
        });
    } else if (this.entityPromise) {
      this._unsubscribe().catch(err => {
        log.debug('ERROR while setting conversation status ' + status, err);
        if (this.services.syncClient.connectionState !== 'disconnected') {
          throw err;
        }
      });
    }
  }

  /**
   * If conversation's status update source
   * @private
   * @return {Conversations.DataSource}
   */
  _statusSource(): Conversations.DataSource {
    return this.statusSource;
  }

  private static preprocessUpdate(update, conversationSid) {
    try {
      if (typeof update.attributes === 'string') {
        update.attributes = JSON.parse(update.attributes);
      } else if (update.attributes) {
        JSON.stringify(update.attributes);
      }
    } catch (e) {
      log.warn('Retrieved malformed attributes from the server for conversation: ' + conversationSid);
      update.attributes = {};
    }

    try {
      if (update.dateCreated) {
        update.dateCreated = new Date(update.dateCreated);
      }
    } catch (e) {
      log.warn('Retrieved malformed dateCreated from the server for conversation: ' + conversationSid);
      delete update.dateCreated;
    }

    try {
      if (update.dateUpdated) {
        update.dateUpdated = new Date(update.dateUpdated);
      }
    } catch (e) {
      log.warn('Retrieved malformed dateUpdated from the server for conversation: ' + conversationSid);
      delete update.dateUpdated;
    }

    try {
      if (update.lastMessage && update.lastMessage.timestamp) {
        update.lastMessage.timestamp = new Date(update.lastMessage.timestamp);
      }
    } catch (e) {
      log.warn('Retrieved malformed lastMessage.timestamp from the server for conversation: ' + conversationSid);
      delete update.lastMessage.timestamp;
    }
  }

  /**
   * Updates local conversation object with new values
   * @private
   */
  _update(update) {
    log.trace('_update', update);

    Conversation.preprocessUpdate(update, this.sid);
    const updateReasons = new Set<Conversation.UpdateReason>();

    for (const key of Object.keys(update)) {
      const localKey = fieldMappings[key];

      if (!localKey) {
        continue;
      }

      switch (localKey) {
        case fieldMappings.status:
          if (!update.status || update.status === 'unknown'
            || this.channelState.status === update.status) {
            break;
          }

          this.channelState.status = update.status;
          updateReasons.add(localKey);

          break;
        case fieldMappings.attributes:
          if (isDeepEqual(this.channelState.attributes, update.attributes)) {
            break;
          }

          this.channelState.attributes = update.attributes;
          updateReasons.add(localKey);

          break;
        case fieldMappings.lastConsumedMessageIndex:
          if (update.lastConsumedMessageIndex === undefined
            || update.lastConsumedMessageIndex === this.channelState.lastReadMessageIndex) {
            break;
          }

          this.channelState.lastReadMessageIndex = update.lastConsumedMessageIndex;
          updateReasons.add('lastReadMessageIndex');

          break;
        case fieldMappings.lastMessage:
          if (this.channelState.lastMessage && !update.lastMessage) {
            delete this.channelState.lastMessage;
            updateReasons.add(localKey);

            break;
          }

          this.channelState.lastMessage = this.channelState.lastMessage || {};

          if (update.lastMessage?.index !== undefined
            && update.lastMessage.index !== this.channelState.lastMessage.index) {
            this.channelState.lastMessage.index = update.lastMessage.index;
            updateReasons.add(localKey);
          }

          if (update.lastMessage?.timestamp !== undefined
            && this.channelState.lastMessage?.dateCreated?.getTime() !== update.lastMessage.timestamp.getTime()) {
            this.channelState.lastMessage.dateCreated = update.lastMessage.timestamp;
            updateReasons.add(localKey);
          }

          if (isDeepEqual(this.channelState.lastMessage, {})) {
            delete this.channelState.lastMessage;
          }

          break;
        case fieldMappings.state:
          const state = update.state || undefined;

          if (state !== undefined) {
            state.dateUpdated = new Date(state.dateUpdated);
          }

          if (isDeepEqual(this.channelState.state, state)) {
            break;
          }

          this.channelState.state = state;
          updateReasons.add(localKey);

          break;
        default:
          const isDate = update[key] instanceof Date;
          const keysMatchAsDates = isDate && this.channelState[localKey]?.getTime() === update[key].getTime();
          const keysMatchAsNonDates = !isDate && this[localKey] === update[key];

          if (keysMatchAsDates || keysMatchAsNonDates) {
            break;
          }

          this.channelState[localKey] = update[key];
          updateReasons.add(localKey);
      }
    }

    if (updateReasons.size > 0) {
      this.emit('updated', { conversation: this, updateReasons: [...updateReasons] });
    }
  }

  /**
   * @private
   */
  private _onMessageAdded(message) {
    for (let participant of this.participants.values()) {
      if (participant.identity === message.author) {
        participant._endTyping();
        break;
      }
    }
    this.emit('messageAdded', message);
  }

  /**
   * Add a participant to the Conversation by its Identity.
   * @param {String} identity - Identity of the Client to add
   * @param {any} [attributes] Attributes to be attached to the participant
   * @returns {Promise<void>}
   */
  @validateTypesAsync(
    nonEmptyString,
    ['undefined', 'string', 'number', 'boolean', 'object', literal(null)]
  )
  async add(identity: string, attributes?: any): Promise<void> {
    return this.participantsEntity.add(identity, attributes);
  }

  /**
   * Add a non-chat participant to the Conversation.
   *
   * @param {String} proxyAddress Proxy (Twilio) address of the participant
   * @param {String} address User address of the participant
   * @param {any} [attributes] Attributes to be attached to the participant
   * @returns {Promise<void>}
   */
  @validateTypesAsync(nonEmptyString, nonEmptyString, ['undefined', 'object'])
  async addNonChatParticipant(proxyAddress: string, address: string, attributes: Record<string, any> = {}) {
    return this.participantsEntity.addNonChatParticipant(proxyAddress, address, attributes);
  }

  /**
   * Advance Conversation's last read Message index to current read horizon.
   * Rejects if User is not Participant of Conversation.
   * Last read Message index is updated only if new index value is higher than previous.
   * @param {Number} index - Message index to advance to as last read
   * @returns {Promise<number>} resulting unread messages count in the conversation
   */
  @validateTypesAsync(nonNegativeInteger)
  async advanceLastReadMessageIndex(index: number): Promise<number> {
    await this._subscribeStreams();
    return this.services.readHorizon.advanceLastReadMessageIndexForConversation(this.sid, index, this.lastReadMessageIndex);
  }

  /**
   * Delete the Conversation and unsubscribe from its events.
   * @returns {Promise<Conversation>}
   */
  async delete(): Promise<Conversation> {
    await this.services.session.addCommand('destroyChannel', {
      channelSid: this.sid
    });
    return this;
  }

  /**
   * Get the custom attributes of this Conversation.
   * @returns {Promise<any>} attributes of this Conversation
   */
  async getAttributes(): Promise<any> {
    await this._subscribe();
    return this.attributes;
  }

  /**
   * Returns messages from conversation using paginator interface.
   * @param {Number} [pageSize=30] Number of messages to return in single chunk
   * @param {Number} [anchor] - Index of newest Message to fetch. From the end by default
   * @param {('backwards'|'forward')} [direction=backwards] - Query direction. By default it query backwards
   *                                                          from newer to older. 'forward' will query in opposite direction
   * @returns {Promise<Paginator<Message>>} page of messages
   */
  @validateTypesAsync(
    ['undefined', nonNegativeInteger],
    ['undefined', nonNegativeInteger],
    ['undefined', literal('backwards', 'forward')]
  )
  async getMessages(pageSize?: number, anchor?: number, direction?: 'backwards' | 'forward'): Promise<Paginator<Message>> {
    await this._subscribeStreams();
    return this.messagesEntity.getMessages(pageSize, anchor, direction);
  }

  /**
   * Get a list of all Participants joined to this Conversation.
   * @returns {Promise<Participant[]>}
   */
  async getParticipants(): Promise<Participant[]> {
    await this._subscribeStreams();
    return this.participantsEntity.getParticipants();
  }

  /**
   * Get conversation participants count.
   * <br/>
   * This method is semi-realtime. This means that this data will be eventually correct,
   * but will also possibly be incorrect for a few seconds. The Conversation system does not
   * provide real time events for counter values changes.
   * <br/>
   * So this is quite useful for any UI badges, but is not recommended
   * to build any core application logic based on these counters being accurate in real time.
   * @returns {Promise<number>}
   */
  async getParticipantsCount(): Promise<number> {
    let links = await this.services.session.getSessionLinks();
    let url = new UriBuilder(links.publicChannelsUrl).path(this.sid).build();
    let response = await this.services.network.get(url);
    return response.body.members_count;
  }

  /**
   * Get a Participant by its SID.
   * @param {String} participantSid - Participant sid
   * @returns {Promise<Participant>}
   */
  @validateTypesAsync(nonEmptyString)
  async getParticipantBySid(participantSid: string): Promise<Participant> {
    return this.participantsEntity.getParticipantBySid(participantSid);
  }

  /**
   * Get a Participant by its identity.
   * @param {String} identity - Participant identity
   * @returns {Promise<Participant>}
   */
  @validateTypesAsync(nonEmptyString)
  async getParticipantByIdentity(identity: string): Promise<Participant> {
    return this.participantsEntity.getParticipantByIdentity(identity);
  }

  /**
   * Get total message count in a conversation.
   * <br/>
   * This method is semi-realtime. This means that this data will be eventually correct,
   * but will also possibly be incorrect for a few seconds. The Conversations system does not
   * provide real time events for counter values changes.
   * <br/>
   * So this is quite useful for any UI badges, but is not recommended
   * to build any core application logic based on these counters being accurate in real time.
   * @returns {Promise<number>}
   */
  async getMessagesCount(): Promise<number> {
    let links = await this.services.session.getSessionLinks();
    let url = new UriBuilder(links.publicChannelsUrl).path(this.sid).build();
    let response = await this.services.network.get(url);
    return response.body.messages_count;
  }

  /**
   * Get unread messages count for the User if they are a Participant of this Conversation.
   * Rejects if the User is not a Participant of the Conversation.
   * <br/>
   * Use this method to obtain the number of unread messages together with
   * updateLastReadMessageIndex() instead of relying on the
   * Message indices which may have gaps. See Message.index for details.
   * <br/>
   * This method is semi-realtime. This means that this data will be eventually correct,
   * but will also possibly be incorrect for a few seconds. The Chat system does not
   * provide real time events for counter values changes.
   * <br/>
   * This is quite useful for any “unread messages count” badges, but is not recommended
   * to build any core application logic based on these counters being accurate in real time.
   * @returns {Promise<number|null>}
   */
  async getUnreadMessagesCount(): Promise<number | null> {
    let links = await this.services.session.getSessionLinks();
    let url = new UriBuilder(links.myChannelsUrl).arg('ChannelSid', this.sid).build();
    let response = await this.services.network.get(url);
    if (response.body.channels.length && response.body.channels[0].channel_sid == this.sid) {
      if ((typeof response.body.channels[0].unread_messages_count !== 'undefined') && response.body.channels[0].unread_messages_count != null) {
        return response.body.channels[0].unread_messages_count;
      }
      return null;
    }

    throw new Error('Conversation is not in user conversations list');
  }

  /**
   * Join the Conversation and subscribe to its events.
   * @returns {Promise<Conversation>}
   */
  async join(): Promise<Conversation> {
    await this.services.session.addCommand('joinChannelV2', { channelSid: this.sid });
    return this;
  }

  /**
   * Leave the Conversation.
   * @returns {Promise<Conversation>}
   */
  async leave(): Promise<Conversation> {
    if (this.channelState.status === 'joined') {
      await this.services.session.addCommand('leaveChannel', { channelSid: this.sid });
    }

    return this;
  }

  /**
   * Remove a Participant from the Conversation. When a string is passed as the argument, it will assume that the string is an identity.
   * @param {String|Participant} participant - identity or participant object to remove
   * @returns {Promise<void>}
   */
  @validateTypesAsync([nonEmptyString, Participant])
  async removeParticipant(participant: string | Participant): Promise<void> {
    if (participant instanceof Participant) {
      await this.participantsEntity.removeBySid(participant.sid);
      return;
    }

    await this.participantsEntity.removeByIdentity(participant as string);
  }

  /**
   * Send a Message in the Conversation.
   * @param {String|FormData|Conversation#SendMediaOptions|null} message - The message body for text message,
   * FormData or MediaOptions for media content. Sending FormData supported only with browser engine
   * @param {any} [messageAttributes] - attributes for the message
   * @param {Conversation#SendEmailOptions} [emailOptions] - email options for the message
   * @returns {Promise<number>} new Message's index in the Conversation's messages list
   */
  @validateTypesAsync(
    [
      'string',
      literal(null),
      // Wrapping it into a custom rule is necessary because the FormData class is not available on initialization.
      custom((value) => [value instanceof FormData, 'an instance of FormData']),
      objectSchema('media options', {
        contentType: nonEmptyString,
        media: custom((value) => {
          let isValid = (typeof value === 'string' && value.length > 0) || value instanceof Uint8Array || value instanceof ArrayBuffer;

          if (typeof Blob === 'function') {
            isValid = isValid || value instanceof Blob;
          }

          return [
            isValid,
            'a non-empty string, an instance of Buffer or an instance of Blob'
          ];
        })
      })
    ],
    ['undefined', 'string', 'number', 'boolean', 'object', literal(null)],
    ['undefined', literal(null), objectSchema('email attributes', {
      subject: [nonEmptyString, 'undefined']
    })]
  )
  async sendMessage(message: string | FormData | Conversation.SendMediaOptions | null, messageAttributes?: any,
                    emailOptions?: Conversation.SendEmailOptions): Promise<number> {
    if (typeof message === 'string' || message === null) {
      let response = await this.messagesEntity.send(message, messageAttributes, emailOptions);
      return parseToNumber(response.messageId);
    }

    let response = await this.messagesEntity.sendMedia(message, messageAttributes, emailOptions);
    return parseToNumber(response.messageId);
  }

  /**
   * Set last read Conversation's Message index to last known Message's index in this Conversation.
   * @returns {Promise<number>} resulting unread messages count in the conversation
   */
  async setAllMessagesRead(): Promise<number> {
    await this._subscribeStreams();

    let messagesPage = await this.getMessages(1);

    if (messagesPage.items.length > 0) {
      return this.advanceLastReadMessageIndex(messagesPage.items[0].index);
    }

    return Promise.resolve(0);
  }

  /**
   * Set all messages in the conversation unread.
   * @returns {Promise<number>} resulting unread messages count in the conversation
   */
  async setAllMessagesUnread(): Promise<number> {
    await this._subscribeStreams();
    return this.services.readHorizon.updateLastReadMessageIndexForConversation(this.sid, null);
  }

  /**
   * Set User Notification level for this conversation.
   * @param {Conversation#NotificationLevel} notificationLevel - The new user notification level
   * @returns {Promise<void>}
   */
  @validateTypesAsync(literal('default', 'muted'))
  async setUserNotificationLevel(notificationLevel: Conversation.NotificationLevel): Promise<void> {
    await this.services.session.addCommand('editNotificationLevel', { channelSid: this.sid, notificationLevel: notificationLevel });
  }

  /**
   * Send a notification to the server indicating that this Client is currently typing in this Conversation.
   * Typing ended notification is sent after a while automatically, but by calling again this method you ensure typing ended is not received.
   * @returns {Promise<void>}
   */
  typing(): Promise<void> {
    return this.services.typingIndicator.send(this.sid);
  }

  /**
   * Update the Conversation's attributes.
   * @param {any} attributes - The new attributes object
   * @returns {Promise<Conversation>}
   */
  @validateTypesAsync(['string', 'number', 'boolean', 'object', literal(null)])
  async updateAttributes(attributes: any): Promise<Conversation> {
    await this.services.session.addCommand('editAttributes', {
      channelSid: this.sid,
      attributes: JSON.stringify(attributes)
    });

    return this;
  }

  /**
   * Update the Conversation's friendlyName.
   * @param {String|null} name - The new Conversation friendlyName
   * @returns {Promise<Conversation>}
   */
  @validateTypesAsync(['string', literal(null)])
  async updateFriendlyName(name: string | null): Promise<Conversation> {
    if (this.channelState.friendlyName !== name) {
      await this.services.session.addCommand('editFriendlyName', {
        channelSid: this.sid,
        friendlyName: name
      });
    }

    return this;
  }

  /**
   * Set Conversation's last read Message index to current read horizon.
   * @param {Number|null} index - Message index to set as last read.
   * If null provided, then the behavior is identical to {@link Conversation#setAllMessagesUnread}
   * @returns {Promise<number>} resulting unread messages count in the conversation
   */
  @validateTypesAsync([literal(null), nonNegativeInteger])
  async updateLastReadMessageIndex(index: number | null): Promise<number> {
    await this._subscribeStreams();
    return this.services.readHorizon.updateLastReadMessageIndexForConversation(this.sid, index);
  }

  /**
   * Update the Conversation's unique name.
   * @param {String|null} uniqueName - New unique name for the Conversation. Setting unique name to null removes it.
   * @returns {Promise<Conversation>}
   */
  @validateTypesAsync(['string', literal(null)])
  async updateUniqueName(uniqueName: string | null): Promise<Conversation> {
    if (this.channelState.uniqueName !== uniqueName) {
      if (!uniqueName) {
        uniqueName = '';
      }

      await this.services.session.addCommand('editUniqueName', {
        channelSid: this.sid,
        uniqueName: uniqueName
      });
    }

    return this;
  }
}

export { ConversationDescriptor, Conversation };

/**
 * Fired when a Participant has joined the Conversation.
 * @event Conversation#participantJoined
 * @type {Participant}
 */
/**
 * Fired when a Participant has left the Conversation.
 * @event Conversation#participantLeft
 * @type {Participant}
 */
/**
 * Fired when a Participant's fields has been updated.
 * @event Conversation#participantUpdated
 * @type {Object}
 * @property {Participant} participant - Updated Participant
 * @property {Participant#UpdateReason[]} updateReasons - Array of Participant's updated event reasons
 */
/**
 * Fired when a new Message has been added to the Conversation.
 * @event Conversation#messageAdded
 * @type {Message}
 */
/**
 * Fired when Message is removed from Conversation's message list.
 * @event Conversation#messageRemoved
 * @type {Message}
 */
/**
 * Fired when an existing Message's fields are updated with new values.
 * @event Conversation#messageUpdated
 * @type {Object}
 * @property {Message} message - Updated Message
 * @property {Message#UpdateReason[]} updateReasons - Array of Message's updated event reasons
 */
/**
 * Fired when a Participant has stopped typing.
 * @event Conversation#typingEnded
 * @type {Participant}
 */
/**
 * Fired when a Participant has started typing.
 * @event Conversation#typingStarted
 * @type {Participant}
 */
/**
 * Fired when a Conversation's attributes or metadata have been updated.
 * @event Conversation#updated
 * @type {Object}
 * @property {Conversation} conversation - Updated Conversation
 * @property {Conversation#UpdateReason[]} updateReasons - Array of Conversation's updated event reasons
 */
/**
 * Fired when the Conversation was destroyed or currently logged in User has left private Conversation
 * @event Conversation#removed
 * @type {Conversation}
 */
import { EventEmitter } from 'events';
import { Logger } from '../logger';
import { Conversation } from '../conversation';

import { SyncMap, SyncClient } from 'twilio-sync';
import { Session } from '../session';
import { SyncList } from '../synclist';
import { Users } from './users';
import { Network } from '../services/network';
import { TypingIndicator } from '../services/typingindicator';
import { ReadHorizon } from '../services/readhorizon';
import { McsClient } from 'twilio-mcs-client';
import { Deferred } from '../util/deferred';
import { Participant } from '../participant';
import { Message } from '../message';
import { isDeepEqual, parseAttributes, parseTime, UriBuilder } from '../util';

const log = Logger.scope('Conversations');

export interface ConversationsServices {
  session: Session;
  syncClient: SyncClient;
  syncList: SyncList;
  users: Users;
  typingIndicator: TypingIndicator;
  readHorizon: ReadHorizon;
  network: Network;
  mcsClient: McsClient;
}

/**
 * Represents conversations collection
 * {@see Conversation}
 */
class Conversations extends EventEmitter {

  private services: ConversationsServices;
  public readonly conversations: Map<string, Conversation>;
  private readonly thumbstones: Set<string>; // sids
  private syncListFetched: boolean;
  public readonly syncListRead: Deferred<boolean>;

  constructor(services: ConversationsServices) {
    super();
    this.services = services;
    this.conversations = new Map<string, Conversation>();
    this.thumbstones = new Set<string>();
    this.syncListFetched = false;
    this.syncListRead = new Deferred<boolean>();
  }

  private getMap(): Promise<SyncMap> {
    return this.services.session.getMyConversationsId()
               .then(name => this.services.syncClient.map({ id: name, mode: 'open_existing' }));
  }

  /**
   * Add conversation to server
   * @private
   * @returns {Promise<Conversation>} Conversation
   */
  async addConversation(options): Promise<Conversation> {
    let attributes;
    if (typeof options.attributes === 'undefined') {
      attributes = {};
    } else {
      attributes = options.attributes;
    }

    let response = await this.services.session.addCommand('createConversation', {
      friendlyName: options.friendlyName,
      uniqueName: options.uniqueName,
      attributes: JSON.stringify(attributes)
    }) as Object;

    let conversationSid = 'conversationSid' in response ? response['conversationSid'] : null;
    let conversationDocument = 'conversation' in response ? response['conversation'] : null;

    let existingConversation = this.conversations.get(conversationSid);
    if (existingConversation) {
      await existingConversation._subscribe();
      return existingConversation;
    }

    let conversation = new Conversation(this.services,
      {
        channel: conversationDocument,

        entityName: null,
        uniqueName: null,
        attributes: null,
        createdBy: null,
        friendlyName: null,
        lastConsumedMessageIndex: null,
        dateCreated: null,
        dateUpdated: null
      },
      conversationSid);

    this.conversations.set(conversation.sid, conversation);
    this.registerForEvents(conversation);

    await conversation._subscribe();
    this.emit('conversationAdded', conversation);
    return conversation;
  }

  /**
   * Fetch conversations list and instantiate all necessary objects
   */
  fetchConversations() {
    this.getMap()
        .then(async map => {
          map.on('itemAdded', args => {
            log.debug('itemAdded: ' + args.item.key);
            this.upsertConversation('sync', args.item.key, args.item.data);
          });

          map.on('itemRemoved', args => {
            log.debug('itemRemoved: ' + args.key);
            let sid = args.key;
            if (!this.syncListFetched) {
              this.thumbstones.add(sid);
            }
            let conversation = this.conversations.get(sid);
            if (conversation) {
              if (conversation && conversation.status === 'joined' /*|| conversation.status === 'invited'*/) {
                conversation._setStatus('notParticipating', 'sync');
                this.emit('conversationLeft', conversation);
              }

              this.conversations.delete(sid);
              this.emit('conversationRemoved', conversation);
              conversation.emit('removed', conversation);
            }
          });

          map.on('itemUpdated', args => {
            log.debug('itemUpdated: ' + args.item.key);
            this.upsertConversation('sync', args.item.key, args.item.data);
          });

          let upserts = [];

          let paginator = await this.services.syncList.getPage();
          let items = paginator.items;
          items.forEach(item => {
            upserts.push(this.upsertConversation('synclist', item.channel_sid, item));
          });

          while (paginator.hasNextPage) {
            paginator = await paginator.nextPage();
            paginator.items.forEach(item => {
              upserts.push(this.upsertConversation('synclist', item.channel_sid, item));
            });
          }

          this.syncListRead.set(true);

          return Promise.all(upserts);
        })
        .then(() => {
          this.syncListFetched = true;
          this.thumbstones.clear();
          log.debug('Conversations list fetched');
        })
        .then(() => this)
        .catch(e => {
          if (this.services.syncClient.connectionState != 'disconnected') {
            log.error('Failed to get conversations list', e);
          }
          log.debug('ERROR: Failed to get conversations list', e);
          throw e;
        });
  }

  private _wrapPaginator(page, op) {
    return op(page.items)
      .then(items => ({
        items: items,
        hasNextPage: page.hasNextPage,
        hasPrevPage: page.hasPrevPage,
        nextPage: () => page.nextPage().then(x => this._wrapPaginator(x, op)),
        prevPage: () => page.prevPage().then(x => this._wrapPaginator(x, op))
      }));
  }

  getConversations(args) {
    return this.getMap()
               .then(conversationsMap => conversationsMap.getItems(args))
               .then(page => this._wrapPaginator(page
                 , items => Promise.all(items.map(item => this.upsertConversation('sync', item.key, item.data))))
               );
  }

  getConversation(sid: string): Promise<Conversation> {
    return this.getMap()
      .then(conversationsMap => conversationsMap.getItems({ key: sid }))
      .then(page => page.items.map(item => this.upsertConversation('sync', item.key, item.data)))
      .then(items => items.length > 0 ? items[0] : null);
  }

  async getConversationByUniqueName(uniqueName: string): Promise<Conversation> {
    const links = await this.services.session.getSessionLinks();
    const url = new UriBuilder(links.myChannelsUrl).path(uniqueName).build();
    const response = await this.services.network.get(url);
    const body = response.body;

    const sid = body.channel_sid;
    const data = {
      entityName: null,
      lastConsumedMessageIndex: body.last_consumed_message_index,
      status: body?.status || 'unknown',
      friendlyName: body.friendly_name,
      dateUpdated: body.date_updated,
      dateCreated: body.date_created,
      uniqueName: body.unique_name,
      createdBy: body.created_by,
      attributes: body.attributes,
      channel: `${sid}.channel`,
      notificationLevel: body?.notification_level,
      sid
    };

    return this.upsertConversation('sync', sid, data);
  }

  async getWhisperConversation(sid: string): Promise<Conversation> {
    const links = await this.services.session.getSessionLinks();
    const url = new UriBuilder(links.publicChannelsUrl).path(sid).build();
    const response = await this.services.network.get(url);
    const body = response.body;

    if (body.type !== 'private') {
      return;
    }

    // todo: refactor this after the back-end change.

    // Currently, a conversation that is created using a non-conversations-specific
    // endpoint (i.e., a chat-specific endpoint) will not have a state property set.
    // The back-end team will fix this, but only when they get some more time to work
    // on this. For now, the SDK will assume that the default state is active when
    // the property is absent from the REST response. The back-end team also mentioned
    // that the state property will become a proper JSON object, as opposed to a JSON
    // string, which is also covered in the following code.

    let state: {
      'state.v1'?: {
        current?: 'active' | 'inactive' | 'closed';
      }
    } | undefined;

    // If the state property is a string, it's expected to be a string that represents
    // a JSON object.

    if (typeof body.state === 'string') {
      state = JSON.parse(body.state);
    }

    // If the state property is already a non-nullable object, then no JSON parsing is
    // required.

    if (typeof body.state === 'object' && body.state !== null) {
      state = body.state;
    }

    if (state?.['state.v1']?.current === 'closed') {
      return;
    }

    const data = {
      entityName: null,
      lastConsumedMessageIndex: body.last_consumed_message_index,
      status: body?.status || 'unknown',
      friendlyName: body.friendly_name,
      dateUpdated: body.date_updated,
      dateCreated: body.date_created,
      uniqueName: body.unique_name,
      createdBy: body.created_by,
      attributes: body.attributes,
      channel: `${sid}.channel`,
      notificationLevel: body?.notification_level,
      sid
    };

    return this.upsertConversation('sync', sid, data);
  }

  private upsertConversation(source: Conversations.DataSource, sid: string, data): Promise<Conversation> {
    log.trace('upsertConversation(sid=' + sid + ', data=', data);
    let conversation = this.conversations.get(sid);

    // Update the Conversation's status if we know about it
    if (conversation) {
      log.trace('upsertConversation: conversation ' + sid + ' is known and it\'s' +
        ' status is known from source ' + conversation._statusSource() +
        ' and update came from source ' + source, conversation);
      if (typeof conversation._statusSource() === 'undefined'
        || source === conversation._statusSource()
        || (source === 'synclist' && conversation._statusSource() !== 'sync')
        || source === 'sync') {
        if (data.status === 'joined' && conversation.status !== 'joined') {
          conversation._setStatus('joined', source);

          let updateData: any = {};

          if (typeof data.notificationLevel !== 'undefined') {
            updateData.notificationLevel = data.notificationLevel;
          }

          if (typeof data.lastConsumedMessageIndex !== 'undefined') {
            updateData.lastConsumedMessageIndex = data.lastConsumedMessageIndex;
          }

          if (!isDeepEqual(updateData, {})) {
            conversation._update(updateData);
          }

          conversation._subscribe().then(() => { this.emit('conversationJoined', conversation); });
        } else if (data.status === 'notParticipating' && conversation.status === 'joined') {
          conversation._setStatus('notParticipating', source);
          conversation._update(data);
          conversation._subscribe().then(() => { this.emit('conversationLeft', conversation); });
        } else if (data.status === 'notParticipating') {
          conversation._subscribe();
        } else {
          conversation._update(data);
        }
      } else {
        log.trace('upsertConversation: conversation is known from sync and came from chat, ignoring', {
          sid: sid,
          data: data.status,
          conversation: conversation.status
        });

      }
      return conversation._subscribe().then(() => conversation);
    }

    if ((source === 'chat' || source === 'synclist') && this.thumbstones.has(sid)) {
      // if conversation was deleted, we ignore it
      log.trace('upsertConversation: conversation is deleted and came again from chat, ignoring', sid);
      return;
    }

    // Fetch the Conversation if we don't know about it
    log.trace('upsertConversation: creating local conversation object with sid ' + sid, data);
    conversation = new Conversation(this.services, data, sid);
    this.conversations.set(sid, conversation);
    return conversation._subscribe().then(() => {
      this.registerForEvents(conversation);
      this.emit('conversationAdded', conversation);
      if (data.status === 'joined') {
        conversation._setStatus('joined', source);
        this.emit('conversationJoined', conversation);
      }
      return conversation;
    });
  }

  private onConversationRemoved(sid: string) {
    let conversation = this.conversations.get(sid);
    if (conversation) {
      this.conversations.delete(sid);
      this.emit('conversationRemoved', conversation);
    }
  }

  private registerForEvents(conversation) {
    conversation.on('removed', () => this.onConversationRemoved(conversation.sid));
    conversation.on('updated', (args: Conversation.UpdatedEventArgs) => this.emit('conversationUpdated', args));
    conversation.on('participantJoined', this.emit.bind(this, 'participantJoined'));
    conversation.on('participantLeft', this.emit.bind(this, 'participantLeft'));
    conversation.on('participantUpdated', (args: Participant.UpdatedEventArgs) => this.emit('participantUpdated', args));
    conversation.on('messageAdded', this.emit.bind(this, 'messageAdded'));
    conversation.on('messageUpdated', (args: Message.UpdatedEventArgs) => this.emit('messageUpdated', args));
    conversation.on('messageRemoved', this.emit.bind(this, 'messageRemoved'));
    conversation.on('typingStarted', this.emit.bind(this, 'typingStarted'));
    conversation.on('typingEnded', this.emit.bind(this, 'typingEnded'));
  }
}

namespace Conversations {
  export type DataSource = 'sync' | 'chat' | 'synclist';
}

export { Conversation, Conversations };
import { EventEmitter } from 'events';

import { Logger } from './logger';
import { Session } from './session';
import { SyncClient } from 'twilio-sync';
import { isDeepEqual, parseAttributes } from './util';
import { validateTypesAsync, literal } from 'twilio-sdk-type-validator';

const log = Logger.scope('User');

interface UserState {
  identity: string;
  entityName: string;
  friendlyName: string;
  attributes: any;
  online: boolean;
  notifiable: boolean;
}

export interface UserServices {
  session: Session;
  syncClient: SyncClient;
}

namespace User {
  export type SubscriptionState = 'initializing' | 'subscribed' | 'unsubscribed';

  export type UpdateReason = 'friendlyName' | 'attributes' | 'reachabilityOnline' | 'reachabilityNotifiable';

  export interface UpdatedEventArgs {
    user: User;
    updateReasons: User.UpdateReason[];
  }
}

/**
 * @classdesc Extended user information.
 * Note that <code>isOnline</code> and <code>isNotifiable</code> properties are eligible
 * to use only if reachability function is enabled.
 * You may check if it is enabled by reading value of {@link Client}'s <code>reachabilityEnabled</code> property.
 *
 * @property {String} identity - User identity
 * @property {String} friendlyName - User friendly name, null if not set
 * @property {any} attributes - Object with custom attributes for user
 * @property {Boolean} isOnline - User real-time conversation connection status
 * @property {Boolean} isNotifiable - User push notification registration status
 * @property {Boolean} isSubscribed - Check if this user receives real-time status updates
 *
 * @fires User#updated
 * @fires User#userSubscribed
 * @fires User#userUnsubscribed
 *
 * @constructor
 * @param {String} identity - Identity of user
 * @param {String} entityId - id of user's object
 * @param {Object} datasync - datasync service
 * @param {Object} session - session service
 */
class User extends EventEmitter {

  private entity: any;
  private services: UserServices;
  private state: UserState;
  private promiseToFetch: Promise<User>;
  private subscribed: User.SubscriptionState;

  /**
   * The update reason for <code>updated</code> event emitted on User
   * @typedef {('friendlyName' | 'attributes' | 'reachabilityOnline' | 'reachabilityNotifiable')} User#UpdateReason
   */

  constructor(identity: string, entityName: string, services: UserServices) {
    super();

    this.subscribed = 'initializing';
    this.setMaxListeners(0);

    this.services = services;

    this.state = {
      identity: identity,
      entityName: entityName,
      friendlyName: null,
      attributes: {},
      online: null,
      notifiable: null
    };
  }

  public get identity(): string { return this.state.identity; }

  public set identity(identity: string) { this.state.identity = identity; }

  public set entityName(name: string) { this.state.entityName = name; }

  public get attributes() { return this.state.attributes; }

  public get friendlyName(): string { return this.state.friendlyName; }

  public get isOnline(): boolean { return this.state.online; }

  public get isNotifiable(): boolean { return this.state.notifiable; }

  public get isSubscribed(): boolean { return this.subscribed == 'subscribed'; }

  // Handles service updates
  _update(key: string, value: any) {
    let updateReasons: User.UpdateReason[] = [];
    log.debug('User for', this.state.identity, 'updated:', key, value);
    switch (key) {
      case 'friendlyName':
        if (this.state.friendlyName !== value.value) {
          updateReasons.push('friendlyName');
          this.state.friendlyName = value.value;
        }
        break;
      case 'attributes':
        const updateAttributes = parseAttributes(value.value, `Retrieved malformed attributes from the server for user: ${this.state.identity}`, log);
        if (!isDeepEqual(this.state.attributes, updateAttributes)) {
          this.state.attributes = updateAttributes;
          updateReasons.push('attributes');
        }
        break;
      case 'reachability':
        if (this.state.online !== value.online) {
          this.state.online = value.online;
          updateReasons.push('reachabilityOnline');
        }
        if (this.state.notifiable !== value.notifiable) {
          this.state.notifiable = value.notifiable;
          updateReasons.push('reachabilityNotifiable');
        }
        break;
      default:
        return;
    }
    if (updateReasons.length > 0) {
      this.emit('updated', { user: this, updateReasons: updateReasons });
    }
  }

  // Fetch reachability info
  _updateReachabilityInfo(map, update) {
    if (!this.services.session.reachabilityEnabled) {
      return Promise.resolve();
    }

    return map.get('reachability')
              .then(update)
              .catch(err => { log.warn('Failed to get reachability info for ', this.state.identity, err); });
  }

  // Fetch user
  async _fetch() {
    if (!this.state.entityName) {
      return this;
    }

    this.promiseToFetch = this.services.syncClient.map({ id: this.state.entityName, mode: 'open_existing', includeItems: true })
                              .then(map => {
                                this.entity = map;
                                map.on('itemUpdated', args => {
                                  log.debug(this.state.entityName + ' (' + this.state.identity + ') itemUpdated: ' + args.item.key);
                                  return this._update(args.item.key, args.item.data);
                                });
                                return Promise.all([
                                  map.get('friendlyName')
                                     .then(item => this._update(item.key, item.data)),
                                  map.get('attributes')
                                     .then(item => this._update(item.key, item.data)),
                                  this._updateReachabilityInfo(map,
                                    item => this._update(item.key, item.data))
                                ]);
                              })
                              .then(() => {
                                log.debug('Fetched for', this.identity);
                                this.subscribed = 'subscribed';
                                this.emit('userSubscribed', this);
                                return this;
                              })
                              .catch(err => {
                                this.promiseToFetch = null;
                                throw err;
                              });
    return this.promiseToFetch;
  }

  _ensureFetched() {
    return this.promiseToFetch || this._fetch();
  }

  /**
   * Updates user attributes.
   * @param {any} attributes new attributes for User.
   * @returns {Promise<User>}
   */
  @validateTypesAsync(['string', 'number', 'boolean', 'object', literal(null)])
  public async updateAttributes(attributes: any): Promise<User> {
    if (this.subscribed == 'unsubscribed') {
      throw new Error('Can\'t modify unsubscribed object');
    }

    await this.services.session.addCommand('editUserAttributes', {
      username: this.state.identity,
      attributes: JSON.stringify(attributes)
    });

    return this;
  }

  /**
   * Update Users friendlyName.
   * @param {String|null} friendlyName - Updated friendlyName
   * @returns {Promise<User>}
   */
  @validateTypesAsync(['string', literal(null)])
  public async updateFriendlyName(friendlyName: string | null): Promise<User> {
    if (this.subscribed == 'unsubscribed') {
      throw new Error('Can\'t modify unsubscribed object');
    }

    await this.services.session.addCommand('editUserFriendlyName', {
      username: this.state.identity,
      friendlyName: friendlyName
    });

    return this;
  }

  /**
   * Removes User from subscription list.
   * @returns {Promise<void>} Promise of completion
   */
  async unsubscribe(): Promise<void> {
    if (this.promiseToFetch) {
      await this.promiseToFetch;
      this.entity.close();
      this.promiseToFetch = null;
      this.subscribed = 'unsubscribed';
      this.emit('userUnsubscribed', this);
    }
  }
}

export { User };

/**
 * Fired when User's properties or reachability status have been updated.
 * @event User#updated
 * @type {Object}
 * @property {User} user - Updated User
 * @property {User#UpdateReason[]} updateReasons - Array of User's updated event reasons
 */
/**
 * Fired when Client is subscribed to User.
 * @event User#userSubscribed
 * @type {User}
 */
/**
 * Fired when Client is unsubscribed from this User.
 * @event User#userUnsubscribed
 * @type {User}
 */
import { EventEmitter } from 'events';
import { User } from '../user';
import { Network } from '../services/network';
import { Session } from '../session';
import { SyncClient } from 'twilio-sync';
import { UriBuilder } from '../util';

export interface UsersServices {
  session: Session;
  network: Network;
  syncClient: SyncClient;
}

/**
 * @classdesc Container for known users
 * @fires Users#userUpdated
 */
class Users extends EventEmitter {

  private services: UsersServices;
  private subscribedUsers: Map<string, User>;
  private fifoStack: any;
  private fifoStackMaxLength: number;
  private userUrlPromise: Promise<string>;
  private userUrl: string;
  public readonly myself: User;

  constructor(services: UsersServices) {
    super();
    this.services = services;
    this.fifoStack = [];
    this.fifoStackMaxLength = 100;
    this.myself = new User(null, null, this.services);
    this.myself.on('updated', (args: User.UpdatedEventArgs) => this.emit('userUpdated', args));
    this.myself.on('userSubscribed', () => this.emit('userSubscribed', this.myself));
    this.myself.on('userUnsubscribed', () => {
      this.emit('userUnsubscribed', this.myself);
      this.myself._ensureFetched();
    });
    this.services = services;
    this.subscribedUsers = new Map<string, User>();

    this.userUrlPromise =
      this.services.session.getSessionLinks()
        .then((links) => {
          this.userUrl = links.usersUrl;
          return this.userUrl;
        });

    this.services.session.getMaxUserInfosToSubscribe()
      .then(maxUserInfosToSubscribe => {
          this.fifoStackMaxLength = maxUserInfosToSubscribe;
        }
      );

    this.services.session.getUsersData()
      .then(data => {
        this.myself.identity = data.identity;
        this.myself.entityName = data.user;
        return this.myself._ensureFetched();
      });
  }

  private handleUnsubscribeUser(user: User): void {
    if (this.subscribedUsers.has(user.identity)) {
      this.subscribedUsers.delete(user.identity);
    }
    let foundItemIndex = -1;
    let foundItem = this.fifoStack.find((item, index) => {
      if (item == user.identity) {
        foundItemIndex = index;
        return true;
      }
      return false;
    });
    if (foundItem) {
      this.fifoStack.splice(foundItemIndex, 1);
    }
    this.emit('userUnsubscribed', user);
  }

  private handleSubscribeUser(user: User): void {
    if (this.subscribedUsers.has(user.identity)) {
      return;
    }
    if (this.fifoStack.length >= this.fifoStackMaxLength) {
      this.subscribedUsers.get(this.fifoStack.shift()).unsubscribe();
    }
    this.fifoStack.push(user.identity);
    this.subscribedUsers.set(user.identity, user);
    this.emit('userSubscribed', user);
  }

  /**
   * Gets user, if it's in subscribed list - then return the user object from it,
   * if not - then subscribes and adds user to the FIFO stack
   * @returns {Promise<User>} Fully initialized user
   */
  async getUser(identity: string, entityName: string = null): Promise<User> {
    await this.services.session.getUsersData();
    await this.myself._ensureFetched();

    if (identity == this.myself.identity) {
      return this.myself;
    }

    let user = this.subscribedUsers.get(identity);
    if (!user) {
      if (!entityName) {
        entityName = await this.getSyncUniqueName(identity);
      }
      user = new User(identity, entityName, this.services);
      user.on('updated', (args: User.UpdatedEventArgs) => this.emit('userUpdated', args));
      user.on('userSubscribed', () => this.handleSubscribeUser(user));
      user.on('userUnsubscribed', () => this.handleUnsubscribeUser(user));
      await user._ensureFetched();
    }

    return user;
  }

  /**
   * @returns {Promise<Array<User>>} returns list of subscribed User objects {@see User}
   */
  async getSubscribedUsers(): Promise<Array<User>> {
    await this.services.session.getUsersData();
    await this.myself._ensureFetched();
    let users = [this.myself];
    this.subscribedUsers.forEach((user) => users.push(user));
    return users;
  }

  /**
   * @returns {Promise<string>} User's sync unique name
   */
  private async getSyncUniqueName(identity: string): Promise<string> {
    const url = new UriBuilder(this.userUrl).path(identity).build();
    let response = await this.services.network.get(url);
    return response.body.sync_unique_name;
  }
}

export { Users };
import { Logger } from '../logger';

import { Notifications } from 'twilio-notifications';

import { NotificationTypes } from '../interfaces/notificationtypes';
import { Transport } from '../interfaces/transport';
import { Configuration } from '../configuration';

const log = Logger.scope('TypingIndicator');

export interface TypingIndicatorServices {
  transport: Transport;
  notificationClient: Notifications;
}

/**
 * An important note in regards to typing timeout timers. There are two places that the SDK can get the "typing_timeout" attribute from. The first
 * place that the attribute appears in is the response received from POST -> /v1/typing REST call. In the body of that response, the value of the
 * "typing_timeout" attribute will be exactly the same as defined in the console. The second place that the attribute appears in is from a
 * notification of type "twilio.ipmsg.typing_indicator". In this case, the "typing_timeout" value will be +1 of that in the console. This
 * intentional. The timeout returned from the POST -> /v1/typing call should be used to disable further calls for that period of time. On contrary,
 * the timeout returned from the notification should be used as the timeout for the "typingEnded" event, +1 is to account for latency.
 *
 * @private
 */

/**
 * @class TypingIndicator
 *
 * @constructor
 * @private
 */
class TypingIndicator {
  private readonly services;
  private readonly config;

  private sentUpdates: Map<string, number>;
  private getConversation;
  private serviceTypingTimeout;

  constructor(config: Configuration, services: TypingIndicatorServices, getConversation) {
    this.config = config;
    this.services = services;
    this.getConversation = getConversation;

    this.serviceTypingTimeout = null;
    this.sentUpdates = new Map();
  }

  public get typingTimeout(): number {
    return this.config.typingIndicatorTimeoutOverride
      || this.serviceTypingTimeout
      || this.config.typingIndicatorTimeoutDefault;
  }

  /**
   * Initialize TypingIndicator controller
   * Registers for needed message types and sets listeners
   * @private
   */
  initialize() {
    this.services.notificationClient.subscribe(NotificationTypes.TYPING_INDICATOR, 'twilsock');
    this.services.notificationClient.on('message', (type, message) => {
      if (type === NotificationTypes.TYPING_INDICATOR) {
        this.handleRemoteTyping(message);
      }
    });
  }

  /**
   * Remote participants typing events handler
   * @private
   */
  handleRemoteTyping(message) {
    log.trace('Got new typing indicator ', message);

    this.getConversation(message.channel_sid)
      .then(conversation => {
        if (!conversation) {
          return;
        }

        conversation.participants.forEach(participant => {
          if (participant.identity !== message.identity) {
            return;
          }

          const timeout = this.config.typingIndicatorTimeoutOverride + 1000 || message.typing_timeout * 1000;
          participant._startTyping(timeout);
        });
      })
      .catch(err => {
        log.error(err);
        throw err;
      });
  }

  /**
   * Send typing event for the given conversation sid
   * @param {String} conversationSid
   */
  send(conversationSid: string) {
    const lastUpdate = this.sentUpdates.get(conversationSid);
    if (lastUpdate && lastUpdate > (Date.now() - this.typingTimeout)) {
      return Promise.resolve();
    }

    this.sentUpdates.set(conversationSid, Date.now());
    return this._send(conversationSid);
  }

  private _send(conversationSid: string) {
    log.trace('Sending typing indicator');

    const url = this.config.typingIndicatorUri;
    const headers = {
      'Content-Type': 'application/x-www-form-urlencoded'
    };
    const body = 'ChannelSid=' + conversationSid;

    return this.services.transport.post(url, headers, body, this.config.productId)
      .then(response => {
        if (response.body.hasOwnProperty('typing_timeout')) {
          this.serviceTypingTimeout = response.body.typing_timeout * 1000;
        }
      }).catch(err => {
        log.error('Failed to send typing indicator:', err);
        throw err;
      });
  }
}

export { TypingIndicator };
import { Session } from '../session';
import { SessionError } from '../sessionerror';

export interface ReadHorizonServices {
  session: Session;
}

class ConsumptionReportRequest {
  entry: ConsumptionReportEntry;
  promises: ReadHorizonPromise[];
}

class ConsumptionReportEntry {
  channelSid: string;
  messageIdx: number;
}

class ReadHorizonPromise {
  resolve: any;
  reject: any;
}

interface ConsumptionReportResponseEntry {
  channelSid: string;
  unreadMessagesCount?: number;
}

/**
 * @classdesc Provides read horizon management functionality
 */
class ReadHorizon {
  private readonly services: ReadHorizonServices;
  private readonly readHorizonRequests: Map<string, ConsumptionReportRequest>;
  private readHorizonUpdateTimer: any;

  constructor(services: ReadHorizonServices) {
    this.services = services;
    this.readHorizonRequests = new Map();
    this.readHorizonUpdateTimer = null;
  }

  private getReportInterval(): Promise<number> {
    return this.services.session.getConsumptionReportInterval().then(
      seconds => seconds * 1000);
  }

  private delayedSendReadHorizon(delay) {
    if (this.readHorizonUpdateTimer !== null) {
      return;
    }

    this.sendConsumptionReport(true);

    this.readHorizonUpdateTimer = setTimeout(() => {
      this.sendConsumptionReport(false);
    }, delay);

  }

  private sendConsumptionReport(keepTimer: boolean) {
    let reports = [];
    let promises = new Map<string, ReadHorizonPromise[]>();
    this.readHorizonRequests.forEach((request, conversationSid) => {
      reports.push(request.entry);
      promises.set(conversationSid, request.promises);
    });
    if (reports.length > 0) {
      this.services.session.addCommand('consumptionReportV2', { report: reports })
          .then(response => this.processConsumptionReportResponse(response, promises))
          .catch(err => this.processConsumptionReportError(err, promises));
    }
    if (!keepTimer) {
      this.readHorizonUpdateTimer = null;
    }
    this.readHorizonRequests.clear();

  }

  private processConsumptionReportResponse(response: any, promises: Map<string, ReadHorizonPromise[]>) {
    if (response && response.report && Array.isArray(response.report) && response.report.length > 0) {
      response.report.forEach(entry => {
        let responseEntry = entry as ConsumptionReportResponseEntry;
        if (promises.has(responseEntry.channelSid)) {
          let unreadMessagesCount = null;
          if ((typeof responseEntry.unreadMessagesCount !== 'undefined') && responseEntry.unreadMessagesCount != null) {
            unreadMessagesCount = responseEntry.unreadMessagesCount;
          }
          promises.get(responseEntry.channelSid).forEach(promise => promise.resolve(unreadMessagesCount));
          promises.delete(responseEntry.channelSid);
        }
      });
    }

    this.processConsumptionReportError(new SessionError('Error while setting LastReadMessageIndex', null), promises);
  }

  private processConsumptionReportError(err: SessionError, promises: Map<string, ReadHorizonPromise[]>) {
    promises.forEach(conversationPromises => conversationPromises.forEach(promise => promise.reject(err)));
  }

  /**
   * Updates read horizon value without any checks
   */
  updateLastReadMessageIndexForConversation(conversationSid: string, messageIdx: number): Promise<number> {
    return new Promise<number>((resolve, reject) => {
      this.addPendingConsumptionHorizonRequest(conversationSid, { channelSid: conversationSid, messageIdx }, { resolve, reject });
      this.getReportInterval().then(delay => this.delayedSendReadHorizon(delay));
    });
  }

  /**
   * Move read horizon forward
   */
  advanceLastReadMessageIndexForConversation(
    conversationSid: string,
    messageIdx: number,
    currentConversationLastReadIndex: number
  ): Promise<number> {
    let currentHorizon = this.readHorizonRequests.get(conversationSid);
    return new Promise<number>((resolve, reject) => {
      if (currentHorizon && currentHorizon.entry) {
        if (currentHorizon.entry.messageIdx >= messageIdx) {
          this.addPendingConsumptionHorizonRequest(conversationSid, currentHorizon.entry, { resolve, reject });
        } else {
          this.addPendingConsumptionHorizonRequest(conversationSid, { channelSid: conversationSid, messageIdx }, { resolve, reject });
        }
      } else {
        if ((currentConversationLastReadIndex !== null) && messageIdx < currentConversationLastReadIndex) {
          this.addPendingConsumptionHorizonRequest(
            conversationSid,
            { channelSid: conversationSid, messageIdx: currentConversationLastReadIndex },
            { resolve, reject });
        } else {
          this.addPendingConsumptionHorizonRequest(conversationSid, { channelSid: conversationSid, messageIdx }, { resolve, reject });
        }
      }
      this.getReportInterval().then(delay => this.delayedSendReadHorizon(delay));
    });
  }

  private addPendingConsumptionHorizonRequest(conversationSid: string, entry: ConsumptionReportEntry, promise: ReadHorizonPromise) {
    if (this.readHorizonRequests.has(conversationSid)) {
      let request = this.readHorizonRequests.get(conversationSid);
      request.entry = entry;
      request.promises.push(promise);
    } else {
      this.readHorizonRequests.set(conversationSid, { entry, promises: [promise] });
    }
  }
}

