import React, { useState } from 'react';
import FilterIcon from '@material-ui/icons/FilterTwoTone';
import { GaussianBlurBackgroundProcessor } from '@twilio/video-processors';

const backgroundFilter = (participant) => {

    const [isBlur, setIsBlur] = useState(false);

    const blurBackground = new GaussianBlurBackgroundProcessor({
        assetsPath: 'assets'
      });

    const handleBlur = () => {
        setIsBlur(!isBlur);
        const videoTrack = participant.videoTrack;
        if(isBlur){
            blurBackground.loadModel().then(() => {
                videoTrack.addProcessor(blurBackground);
            });
        }else{
            videoTrack.removeProcessor(blurBackground);
        }
    }

    return (
        <div className = 'toolbarDiv'>
            <button onClick = {handleBlur}>
                <FilterIcon/>
            </button>
        </div>
    );

};

export default backgroundFilter;
