import React, { useCallback, useState } from 'react';
import Drawer from '@material-ui/core/Drawer';
import IconButton from '@material-ui/core/IconButton';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import ChatIcon from '@material-ui/icons/ChatTwoTone';
import ChatBubbleIcon from '@material-ui/icons/ChatBubbleTwoTone';
import SendTwoToneIcon from '@material-ui/icons/SendTwoTone';
import TextField from '@material-ui/core/TextField';


const drawerWidth = 0;

const useStyles = makeStyles((theme) => ({
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: 240,
    marginRight: 0,
    borderWidth: 'thick',
    borderStyle: 'solid',
    borderColor: 'aquamarine',
    borderRadius: '10px',
    marginTop: '3%',
    height: '92%',
    position: 'fixed'
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    marginRight: 0,
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-start',
  },

}));


const TextChat = ({room}) => {
    const classes = useStyles();
    const theme = useTheme();
    const [isOpen, setIsOpen] = useState(false);
    const [message, setMessage] = useState('');
    const {LocalDataTrack} = require('twilio-video');
    const dataTrack = new LocalDataTrack();

    const handleClick = () => {
        setIsOpen(!isOpen);
    }

    room.localParticipant.publishTrack(dataTrack).catch();

    const dataTrackPublished = {};

    dataTrackPublished.promise = new Promise((resolve, reject) => {
      dataTrackPublished.resolve = resolve;
      dataTrackPublished.reject = reject;
    });

    room.localParticipant.on('trackPublished', publication => {
      if (publication.track === dataTrack) {
        dataTrackPublished.resolve();
      }
    });

    room.localParticipant.on('trackPublicationFailed', (error, track) => {
      if (track === dataTrack) {
        dataTrackPublished.reject(error);
      }
    });

    function sendMessage(event) {
      event.preventDefault();
      const node = document.createElement('div');
      const textnode = document.createTextNode(message);
      node.appendChild(textnode);
      document.getElementById('text-area').appendChild(node);
      setMessage('');
      // dataTrackPublished.promise.then(() => dataTrack.send(message));
    }

    const handleMessageChange = useCallback((event)=>{
      setMessage(event.target.value);
    }
    ,[]);


    return (
        <div>
            <button
                onClick={handleClick}
                style={{background: "#21242c",
                  border: "#383b43",
                  borderStyle: "solid",
                  borderWidth: "1px",
                  padding: "0.45em"
                }}
            >
                {isOpen?<ChatBubbleIcon/>:<ChatIcon/>}
            </button>
            <Drawer
                className = {classes.drawer}
                anchor = 'right'
                variant = 'persistent'
                open = {isOpen}
                classes={{
                    paper: classes.drawerPaper,
                  }}
            >
                <div className={classes.drawerHeader} style = {{marginRight: '0%'}}>
                    <IconButton onClick={handleClick}>
                        <ChevronRightIcon/>
                    </IconButton>
                    <p style = {{marginBottom: '0rem'}}>In call messages</p>
                </div>
                <div id = 'text-area'>TEXT</div>
                <footer style = {{display: 'inline-block', position: 'absolute', bottom: '3%', width: '100%', background: '#fff', color: 'black'}}>
                  <form onSubmit = {sendMessage}>
                    <TextField id="message" label="Send message to everyone" variant = 'outlined' value = {message} onChange = {handleMessageChange}></TextField>
                  </form>
                </footer>
            </Drawer>
        </div>
    );
}

export default TextChat;
