import React from "react";
import {
  AppBar,
  Backdrop,
  CircularProgress,
  Container,
  CssBaseline,
  Grid,
  IconButton,
  List,
  TextField,
  Toolbar,
  Typography,
  Button,
  Tooltip,
} from "@material-ui/core";
import VideoCallOutlinedIcon from '@material-ui/icons/VideoCallOutlined';
import ExitToAppOutlinedIcon from '@material-ui/icons/ExitToAppOutlined';
import GroupAddOutlinedIcon from '@material-ui/icons/GroupAddOutlined';
import { Send } from "@material-ui/icons";
import ChatItem from "./ChatItem";
import VideoChat_test from "./VideoChat_test";
const Chat = require("twilio-chat");

class ChatScreen extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      text: "",
      messages: [],
      loading: false,
      channel: null,
      isVideo: false,
    };

    this.scrollDiv = React.createRef();
  };
  getToken = async (email) => {
    var data = await fetch("/chat/token", {
      method: "POST",
      body: JSON.stringify({
        identity: email,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    }).then((res) => res.json());
    return data.token;
  };

  componentDidMount = async () => {
    document.getElementsByTagName('body')[0].style.background = "rgb(255,255,255)";
    const { email, room } = this.props;
    let token = "";

    this.setState({ loading: true });

    try {
      token = await this.getToken(email);
    } catch {
      throw new Error("unable to get token, please reload thissss page");
    }
    const client = await Chat.Client.create(token);

    client.on("tokenAboutToExpire", async () => {
      const token = await this.getToken(email);
      client.updateToken(token);
    });

    client.on("tokenExpired", async () => {
      const token = await this.getToken(email);
      client.updateToken(token);
    });

    // client.on("channelJoined", async (channel) => {
    //   // getting list of all messages since this is an existing channel
    //   const messages = await channel.getMessages();
    //   this.setState({ messages: messages.items || [] });
    //   //   this.scrollToBottom();
    // });

    try {
      console.log(room);
      const channel = await client.getChannelByUniqueName(room);
      await this.joinChannel(channel);
      this.setState({ channel, loading: false });
      const messages = await channel.getMessages();
      this.setState({ messages: messages.items || [] });
    } catch {
      try {
        const channel = await client.createChannel({
          uniqueName: room,
          friendlyName: room,
        });
        await this.joinChannel(channel);
        this.setState({ channel, loading: false });
      } catch {
        throw new Error("unable to create channel, please reload this page");
      }
    }
  };

  joinChannel = async (channel) => {
    if (channel.channelState.status !== "joined") {
      await channel.join();
    }
    channel.on("messageAdded", this.handleMessageAdded);
  };

  handleMessageAdded = (message) => {
    const { messages } = this.state;
    this.setState(
      {
        messages: !!messages ? [...messages, message] : [message],
      },
      //   this.scrollToBottom
    );
  };

  //   scrollToBottom = () => {
  //     const scrollHeight = this.scrollDiv.current.scrollHeight;
  //     const height = this.scrollDiv.current.clientHeight;
  //     const maxScrollTop = scrollHeight - height;
  //     this.scrollDiv.current.scrollTop = maxScrollTop > 0 ? maxScrollTop : 0;
  //   };

  sendMessage = () => {
    const { text, channel } = this.state;
    if (text && String(text).trim()) {
      this.setState({ loading: true });
      channel && channel.sendMessage(text);
      this.setState({ text: "", loading: false });
    }
  };

  sendMessageExternal = (info) => {
    const { channel } = this.state;
    if (info && String(info).trim()) {
      this.setState({ loading: true });
      channel && channel.sendMessage(info);
      this.setState({ loading: false });
    }
  }

  setIsVideo = (val) => {
    this.setState({ isVideo: val });
  }

  handleClick = () => {
    this.setState({ isVideo: true });
  }

  render() {
    const { loading, text, messages, channel, isVideo } = this.state;
    const { email, room, setSubmit } = this.props;
    let component;
    if (isVideo) {
      component = <VideoChat_test username={email} roomName={room} messages={messages}
        setIsVideo={this.setIsVideo} sendMessage={this.sendMessageExternal} />
    } else {
      component = <Container component="main" maxWidth="md">
        <Backdrop open={loading} style={{ zIndex: 99999 }}>
          <CircularProgress style={{ color: "white" }} />
        </Backdrop>
        <AppBar elevation={10}>
          <Toolbar style={{ justifyContent: 'space-between' }}>
            <Typography variant="h6">
              {`Room: ${room}, User: ${email}`}
            </Typography>
            <Grid container direction="row-reverse">
              <Grid item>
                <Tooltip title="Leave">
                  <Button onClick = {() => {setSubmit()}}>
                    <ExitToAppOutlinedIcon style={{ position: 'relative', fontSize: "2.45rem", color: "white", marginTop :'4px' }}></ExitToAppOutlinedIcon>
                  </Button>
                </Tooltip>
              </Grid>
              <Grid item>
                <Tooltip title="Add others">
                  <Button >
                    <GroupAddOutlinedIcon style={{ position: 'relative', fontSize: "xxx-large", color: "white" }}></GroupAddOutlinedIcon>
                  </Button>
                </Tooltip>
              </Grid>
              <Grid item>
                <Tooltip title="Switch to Video call">
                  <Button onClick={this.handleClick}>
                    <VideoCallOutlinedIcon style={{ position: 'relative', fontSize: "xxx-large", color: "white" }}></VideoCallOutlinedIcon>
                  </Button>
                </Tooltip>
              </Grid>
            </Grid>
          </Toolbar>
        </AppBar>
        {/* <CssBaseline /> */}
        <Grid container style = {{width : "100%"}}>
          <Grid item style = {{width :"20%"}}>
            <div style = {{background:"black"}}></div>
          </Grid>
          <Grid item style = {{width: "80%"}}>
          <Grid container direction="column" style={styles.mainGrid}>
          <Grid item style={styles.gridItemChatList} >
            <List dense={true}>
              {messages &&
                messages.map((message) => (
                  <ChatItem
                    key={message.index}
                    message={message}
                    email={email}
                  />
                ))}
            </List>
          </Grid>
          <Grid item style={styles.gridItemMessage}>
            <Grid
              container
              direction="row"
              justify="center"
              alignItems="center"
            >
              <Grid item style={styles.textFieldContainer}>
                <TextField
                  required
                  style={styles.textField}
                  placeholder="Send a message to everyone"
                  variant="outlined"
                  multiline
                  rows={2}
                  value={text}
                  disabled={!channel}
                  onChange={(event) =>
                    this.setState({ text: event.target.value })
                  }
                />
              </Grid>
              <Grid item>
                <IconButton
                  style={styles.sendButton}
                  onClick={this.sendMessage}
                  disabled={!channel || !text}
                >
                  <Send style={styles.sendIcon} />
                </IconButton>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
          </Grid>
        </Grid>

      </Container>
    }

    return (
      component
    );
  }
}

const styles = {
  textField: { width: "100%", borderWidth: 0, borderColor: "transparent" },
  textFieldContainer: { flex: 1, marginRight: 12 },
  gridItem: { paddingTop: 12, paddingBottom: 12 },
  gridItemChatList: { overflow: "auto", height: "70vh" },
  gridItemMessage: { marginTop: 12, marginBottom: 12 },
  sendButton: { backgroundColor: "#3f51b5" },
  sendIcon: { color: "white" },
  mainGrid: { paddingTop: 100, borderWidth: 1 },
};

export default ChatScreen;
