import React, { useEffect } from 'react';
import { Grid } from '@material-ui/core';
import ToggleOffIcon from '@material-ui/icons/ToggleOff';
import ToggleOnIcon from '@material-ui/icons/ToggleOn';
import VideocamIcon from '@material-ui/icons/Videocam';
import { ToggleOff } from '@material-ui/icons';

const DisplayPreview = ({ handleSubmit, connecting, setAudio, setVideo, audio, video}) => {
    const dep = 'x';
    var videoTrack = "";
    useEffect(() => {
        const { createLocalVideoTrack } = require('twilio-video');
        console.log("ST");
        const localMediaContainer = document.getElementById('local-media');
        createLocalVideoTrack().then(track => {
            localMediaContainer.appendChild(track.attach());
            videoTrack = track;
        });
    }, [dep, videoTrack]);

    const handleClick = () => {
        videoTrack.stop();
    }
    const toggleVideo = () => {
        setVideo(!video);
        if(!video){
        const { createLocalVideoTrack } = require('twilio-video');
        const localMediaContainer = document.getElementById('local-media');
        localMediaContainer.removeChild(localMediaContainer.childNodes[0]);
        createLocalVideoTrack().then(track => {
            localMediaContainer.appendChild(track.attach());
            videoTrack = track;
        });
        }else{
            console.log("STOPPED");
            videoTrack.stop();
        }
    }
    const toggleAudio = () => {
        setAudio(!audio);
    }
    return (
        <Grid container direction='column' justifyContent='center' alignItems='center'>
            <Grid item id = 'local-media' className = 'participant'>
                {/* <div id='local-media' className='participant'></div> */}
            </Grid>
            <Grid item>
                <form onSubmit={handleSubmit}>
                    <button type="submit" disabled={connecting} onClick={handleClick}>
                        {connecting ? <div><i className="fa fa-spinner fa-spin"></i> <span>Connecting</span></div> : "Connect"}
                    </button>
                </form>
            </Grid>
            <Grid item>
                <button onClick = {toggleVideo}>
                    <VideocamIcon style = {{display: 'inline-block'}}/>
                    {video?<ToggleOnIcon style = {{display : 'inline-block'}}/>:<ToggleOffIcon style = {{display : 'inline-block'}}/>}
                </button>
            </Grid>
        </Grid>
    );
}
export default DisplayPreview;
